vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

#define FOV 1.56
#define RAD R.x*0.4

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float rho(vec3 pos)
{
    pos.xy += R*0.5;
 	pos.xy = mod(pos.xy, R-1.);
    vec4 v = P(pos.xy);
    return v.z;
}

vec3 color(vec3 pos)
{
    pos.xy += R*0.5;
 	pos.xy = mod(pos.xy, R-1.);

    vec4 v = C(pos.xy);
    return v.xyz;
}


float DE(vec3 pos)
{
    float y = 1.*rho(pos);  

    pos.xy += R*0.5;
 	pos.xy = mod(pos.xy, R-1.);
    float de = 1e10;
    de = min(de, 0.7*sdBox(pos - vec3(R, 4.*y)*0.5, vec3(R*0.51, 3.)));
    return de;
}


vec4 calcNormal(vec3 p, float dx) {
	const vec3 K = vec3(1,-1,0);
	return  (K.xyyx*DE(p + K.xyy*dx) +
			 K.yyxx*DE(p + K.yyx*dx) +
			 K.yxyx*DE(p + K.yxy*dx) +
			 K.xxxx*DE(p + K.xxx*dx))/vec4(4.*dx,4.*dx,4.*dx,4.);
}

#define marches 70.
#define min_d 1.
vec4 ray_march(vec3 p, vec3 r)
{
    float d;
    for(float i = 0.; i < marches; i++)
    {
        d = DE(p); 
        p += r*d;
        if(d < min_d || d > R.x) break;
    }
    return vec4(p, d);
}

