vec2 getDist(vec3 p) {
    vec2 ar = decartToPolar(p.xz);
    float a = ar.x, r = ar.y;
    float astep = 2. * PI / 8.; // number of rays
    a = mod(a, astep);
    a -= astep / 2.;
    a = abs(a);
    p.xz = polarToDecart(vec2(a, r));
