#define DRAW_DISTANCE 100.0
#define M_PI 3.1415926535

#define BSDF_TRACING (uv.x > 0.0)
#define EMISSIVITY vec3 (4.0)


// *******************************************************
// Change sceneLum(vec3 p) to try any of them out
// They converge faster! though with bias. This is because we don't have the exact projected sampling area.
// *******************************************************

float sceneLumSphere (vec3 p) // Sphere
{
    return length(p) - 0.5;
}

float sceneLumBox (vec3 p) // Box
{
    vec3 q = abs(p) - vec3 (0.2);
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sceneLumTorus (vec3 p) // Capped torus
{
    float ra = 1.0;
    float rb = 0.1;
    vec2 sc = vec2 (1.0, 0.025);
    p.x = abs(p.x);
    float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sceneLumLink(vec3 p) // Link
{
    float le = 0.5;
    float r1 = 0.5;
    float r2 = 0.05;
    vec3 q = vec3( p.x, max(abs(p.y)-le,0.0), p.z );
    return length(vec2(length(q.xy)-r1,q.z)) - r2;
}

float sdOctahedron(vec3 p) // Octahedron
{
    float s = 0.5;
    p = abs(p);
    float m = p.x+p.y+p.z-s;
    vec3 q;
    if( 3.0*p.x < m ) q = p.xyz;
    else if( 3.0*p.y < m ) q = p.yzx;
        else if( 3.0*p.z < m ) q = p.zxy;
            else return m*0.57735027;

            float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
        return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float sceneLum(vec3 p)
{
    return sceneLumSphere (p); // <--- Change this to try different ones...
}

// *******************************************************
// *******************************************************
// *******************************************************

float scene (vec3 p, out float mat)
{
    mat = 0.0;
    float walls = p.y + 1.0;
    walls = min (walls, -p.z + 1.0);

    float lamp = sceneLum(p);

    float sceneSDF = min (lamp, walls);

    if ( sceneSDF == lamp ) mat = 1.0;
    else mat = 0.0;

    return sceneSDF;
}

vec3 hash(vec3 p)  // replace this by something better
{
	p = 50.0*fract( p*0.3183099 + vec3(0.71,0.113,0.419));
	float xVal = -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
	float yVal = -1.0+2.0*fract( p.x*p.z*(p.x+p.z) );
	float zVal = -1.0+2.0*fract( p.z*p.y*(p.z+p.y) );
    return vec3(xVal, yVal, zVal);
}

vec3 randOnHemi (vec2 randVar, vec3 surfNorm)
{
    float phi = 2.0*M_PI*randVar.x;
    float theta = acos (1.0 - randVar.y);

    vec3 tanVec = normalize (cross (surfNorm + vec3 (0.1), surfNorm));
    vec3 biTanVec = cross (tanVec, surfNorm);

    vec3 x = sin(theta)*cos(phi)*tanVec;
    vec3 y = sin(theta)*sin(phi)*biTanVec;
    vec3 z = cos(theta)*surfNorm;
    return normalize (x + y + z);
}

bool sphereMarchScene(vec3 startPt, vec3 dir, out vec3 hitPt, out vec3 hitNorm, out float hitMat)
{
    float i = 0.0;
    float hitMatTmp;
    vec3 samplePt = startPt;
    for (i = 0.0; i != 120.0; i += 1.0)
    {
        float curDist = scene (samplePt, hitMat);
        if ( curDist < 0.01 )
        {
            hitNorm.x = scene(samplePt + vec3(0.001, 0.0, 0.0), hitMatTmp) - scene(samplePt - vec3(0.001, 0.0, 0.0), hitMatTmp);
            hitNorm.y = scene(samplePt + vec3(0.0, 0.001, 0.0), hitMatTmp) - scene(samplePt - vec3(0.0, 0.001, 0.0), hitMatTmp);
            hitNorm.z = scene(samplePt + vec3(0.0, 0.0, 0.001), hitMatTmp) - scene(samplePt - vec3(0.0, 0.0, 0.001), hitMatTmp);
            hitNorm = normalize (hitNorm);
            hitPt = samplePt;
            return true;
        }
        samplePt += curDist * dir;
    }
    return false;
}

bool wosLum(vec3 startPt, out vec3 hitPt, out vec3 hitNorm)
{
    float i = 0.0;
    vec3 samplePt = startPt;
    for (i = 0.0; i != 120.0; i += 1.0)
    {
        float curDist = sceneLum (samplePt);
        if ( abs (curDist) < 0.01 )
        {
            hitNorm.x = sceneLum(samplePt + vec3(0.001, 0.0, 0.0)) - sceneLum(samplePt - vec3(0.001, 0.0, 0.0));
            hitNorm.y = sceneLum(samplePt + vec3(0.0, 0.001, 0.0)) - sceneLum(samplePt - vec3(0.0, 0.001, 0.0));
            hitNorm.z = sceneLum(samplePt + vec3(0.0, 0.0, 0.001)) - sceneLum(samplePt - vec3(0.0, 0.0, 0.001));
            hitNorm = normalize (hitNorm);
            hitPt = samplePt;
            return true;
        }
        vec3 dir = normalize (textureLod (iChannel1, samplePt.xz * 3.0 + iTime * samplePt.zy * 3.0, 0.0).rgb * 2.0 - 1.0);
        samplePt += curDist * dir;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uvRaw = fragCoord/iResolution.xy;
    vec2 uv = uvRaw * 2.0 - 1.0;
    uv.y *= iResolution.y/iResolution.x;
	vec2 aa = textureLod(iChannel1, iTime * M_PI * uv, 0.0).rg * 2.0 - 1.0; // LDS jitter
    aa *= 1.05;
    uv += aa / iResolution.xy;

    vec4 mouseInfo = iMouse / iResolution.xxxx;

    vec3 curEye = vec3 (cos(mouseInfo.x * 2.0 - M_PI * 0.5), mouseInfo.y * 3.0, sin(mouseInfo.x * 2.0 - M_PI * 0.5)) * 2.0;
    vec3 sampleLook = normalize (-curEye);

    vec3 side = cross (vec3 (0.0, -1.0, 0.0), sampleLook);
    vec3 up = cross (side, sampleLook);
    if ( dot (up, vec3 (0.0, 1.0, 0.0)) < 0.0 ) up = -up;

    const float drawDistSq = DRAW_DISTANCE*DRAW_DISTANCE;
    vec3 sampleDir = normalize (sampleLook + side * uv.x + up * uv.y);
    vec3 samplePt = curEye + sampleDir;

    samplePt = curEye + sampleDir;
    bool samplingLight = false;
    vec3 hitNorm, hitNorm2;
    vec3 hitPoint, hitPoint2;
    float hitMat, hitMat2;
    vec3 colorAccum = vec3 (0.0);

    if ( sphereMarchScene (samplePt, sampleDir, hitPoint, hitNorm, hitMat) )
    {
        if ( hitMat == 1.0 )
        {
            colorAccum = EMISSIVITY;
        }
        else
        {
            if ( BSDF_TRACING )
            {
                vec2 randVar = (hash (hitPoint * 3.0 + vec3(iTime * 3.0)).xz + hash (hitPoint * 3.0 + vec3(iTime * 3.0)).yx + vec2 (1.0)) * 0.5;
                sampleDir = randOnHemi (randVar, hitNorm);
                hitPoint += hitNorm * 0.01;
                if ( sphereMarchScene (hitPoint, sampleDir, hitPoint2, hitNorm2, hitMat2) && hitMat2 == 1.0 )
                {
                    colorAccum = 2.0 * max (dot (hitNorm, sampleDir), 0.0) * EMISSIVITY;
                }
            }
            else
            {
                if ( wosLum (hitPoint, hitPoint2, hitNorm2) )
                {
                    vec3 lightSegment = hitPoint2 - hitPoint;
                    vec3 Lo = normalize (lightSegment);
                    float NddotLo = abs (dot (hitNorm, Lo));
                    float LidotNl = abs (dot (hitNorm2, -Lo));
                    float distSq = dot (lightSegment, lightSegment);
                    float GeomTerm = (NddotLo * LidotNl) / distSq;
                    colorAccum = GeomTerm * 0.75 * EMISSIVITY; // We don't have |A| so just make stuff up...
                }
            }
        }
    }

    vec4 bufA = texture(iChannel0, uvRaw);
    if ( mouseInfo.z > 0.0 )
        bufA = vec4 (colorAccum, 1.0);
    else
        bufA += vec4 (colorAccum, 1.0);
    fragColor = bufA;
}

