    float l0 = length(eye-dmap); //distance to point in last frame

    float l1 = texture(iChannel0, vec2((fragCoord.x), (fragCoord.y))).w; //distance between points in consecutive frames

    float l2;
    //if(l1 >= 1.0){ //how much does the depth change between frames?
    //    l2 = max(log(l0)*2.0,0.0);
    //}
    //else{
        l2 = pow(l0,.9);
    //}

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST+l2, MAX_DIST);

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;
    fragColor = vec4(p, (dist-l0));
    }
