// 2020 with colors and shadows (premultiplied rgba)
vec4 yearCol(vec2 uv)
{
    float angle = sin(iTime) * 0.3;

    vec4 date = twoCol(uv, green, vec2(-2.5, 0.0), angle);
    date = premulBlend(zeroCol(uv, green, vec2(-0.8, 0.0), angle), date);
    date = premulBlend(twoCol(uv, green, vec2(0.8, 0.0), angle), date);
    date = premulBlend(zeroCol(uv, green, vec2(2.5, 0.0), angle), date);

    return  date;
}



float corona(vec2 uv, float blur)
{

    float angle = atan(-uv.x, -uv.y) / 6.2831 + 0.5;

    float copies = 9.0;
    //float step = (1.0 / copies);
    float quarter = floor(angle * copies + 0.5);

    float rot = -quarter * 6.2831 / copies;

    float s = sin(rot);
    float c = cos(rot);

    uv *= mat2(c, s, -s, c);

    float dst = length(uv) - 0.4;


    float dst2 = length((uv - vec2(0.0, 0.63)) * vec2(1.0, 1.2)) - 0.13;

    if (uv.y < 0.63)
    {
        dst = min(dst, abs(uv.x) - 0.06);// + 0.1, 0.1);
    }

    dst = smin(dst, dst2, 0.1);

    return S(-0.01 - blur, 0.01 + blur , dst);
}


// Compute a randomized Bokeh spot inside a grid cell
float coronaSpot(vec2 uv, vec2 id)
{
    float accum = 1.0;

    for (float x = -1.0; x <= 1.0; x += 1.0)
    {
        for (float y = -1.0; y <= 1.0; y += 1.0)
        {
            vec2 offset = vec2(x, y);
            vec2 cellId = id + offset;
            vec4 rnd = N14(mod(cellId.x, 300.0) * 25.3 + mod(cellId.y, 300.0) * 6.67);

            float t = iTime;

            vec2 cellUV = uv - offset + rnd.yz * 1.0 + vec2(sin(t * (rnd.x + 0.3)), cos(t * (rnd.z + 0.2))) * 0.2;


            float rot = rnd.y * rnd.x * 456.0 + iTime * (0.3 * (rnd.y - 0.5)) ;
            float c = cos(rot);
            float s = sin(rot);
            cellUV *= mat2(c,s,-s,c);

            float dst = rnd.y;

            float intensity = corona(cellUV * (1.5 + dst), dst * 0.2);

            accum *= mix(intensity, 1.0, dst);
        }
    }

    return accum;
}

// Computes a random layer of embers spots
float coronaLayer(vec2 uv)
{
    vec2 id = floor(uv);
    vec2 cellUV = (uv - id) - vec2(0.5, 0.5) ;

    return coronaSpot(cellUV, id);
}




// Final composition
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    uv *= 3.0;

    vec4 dateCol =  yearCol(uv); // 2020

    vec3 bg = fire(uv);


    float corona = coronaLayer(uv * 1.1);


    bg.rgb += mix(0.0,  corona, S(-2.0, 7.0, uv.y)) * 0.8;


    //add a bit of light
    dateCol.rgb -= uv.y * 0.15 * dateCol.a;
    bg.rgb -= uv.y * 0.03;

    // blend 2020 and BG
    vec3 col = premulBlend(dateCol, bg);

    // Gamma correction to make the image warmer
    float gamma = 0.8;
    col.rgb = pow(col.rgb, vec3(gamma));

    fragColor = vec4(col,1.0);
}

vec3 cameraPosition;
mat3 cameraMatrix;
vec4 offset;
void qFold (inout vec4 a) {
    a = vec4(a.x*a.x-a.y*a.y-a.z*a.z-a.w*a.w,2.0*a.x*a.y,2.0*a.x*a.z,2.0*a.x*a.w);
}
float map (vec3 pos) {
    vec4 z = vec4(pos.xyz, 0.0);
    float md2 = 1.0;
    float radius2 = dot(z, z);
    for (int i = 0; i < 25; i ++) {
        qFold(z);
        z += offset;
        md2 *= 4.0 * radius2;
        radius2 = dot(z, z);
        if (radius2 > 4.0) break;
    }
    return 0.25 * sqrt(radius2 / md2) * log(radius2) - 0.001;
}
vec3 xDir = vec3(0.000001, 0, 0);
vec3 yDir = vec3(0, 0.000001, 0);
vec3 zDir = vec3(0, 0, 0.000001);
vec3 surfaceNormal (vec3 pos) {
    vec3 normal = vec3(
        map(pos + xDir) - map(pos - xDir),
        map(pos + yDir) - map(pos - yDir),
        map(pos + zDir) - map(pos - zDir)
    );
    return normalize(normal);
}
vec3 lightDirection;
float hue;
vec3 tint;
vec3 trace (vec3 origin, vec3 direction) {
    float totalDistance = 0.0;
    for (float steps = 0.0; steps < 100.0; steps ++) {
        vec3 pos = origin + direction * totalDistance;
        float distance = map(pos);
        totalDistance += distance;
        if (dot(pos, pos) > 25.0) break;
        if (distance < 0.002) {
            vec3 normal = surfaceNormal(pos);
            float diffuse = max(-dot(normal, lightDirection), 0.0);
            float specular = -dot(reflect(direction, normal), lightDirection);
            specular = max(pow(specular, 5.0), 0.0);
            float shade = diffuse * 0.7 + specular * 0.3;
            return shade * tint;
        }
    }
    return vec3(0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    cameraPosition = 1.8 * vec3(cos(iTime), 0, sin(iTime));
	cameraMatrix = mat3(cos(iTime+3.14/2.0), 0, -sin(iTime+3.14/2.0), 0, 1, 0, sin(iTime+3.14/2.0), 0, cos(iTime+3.14/2.0));
	offset = vec4(cos(iTime), sin(iTime), cos(iTime * 2.0 + 3.14), sin(iTime * 1.61 + 3.14)) * 0.6;
    lightDirection = vec3(cos(iTime + 3.14), 0.0, sin(iTime + 3.14));
    tint = vec3(cos(iTime / 20.0 * 3.1415 * 2.0) + 1.0, cos(iTime / 20.0 * 3.1415 * 2.0 + 2.0 * 3.1415 / 3.0) + 1.0, cos(iTime / 20.0 * 3.1415 * 2.0 + 4.0 * 3.1415 / 3.0) + 1.0) / 2.0;
    hue = iTime / 20.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 1.0 - 2.0 * fragCoord/iResolution.xy;
    vec3 ray = normalize(vec3(uv, 1.0)) * cameraMatrix;

    // Time varying pixel color
    vec3 col = trace(cameraPosition, ray);

    // Output to screen
    fragColor = vec4(col,1.0);
}

