                    1. * sin(iTime * 3.5 + 10. * float(i) + 2.) );
        spheres = opSmoothUnion(spheres, length(ps) - .5, 1.5);
    }
    for(int i = 0; i < 4; i++) {
        vec3 ps = p;
        // ps *= 2.;
        ps += vec3( 1. * sin(iTime * 1.7 + 20. * float(i)), 
