// March
# define INTERSECTED 0
# define TOO_FAR 1
# define TOO_MANY_STEPS 2
void marchWorld(
    inout vec3 pos, inout vec3 dir,
    out float dist, in float maxDist, in float minDist,
    out int numSteps, in int maxNumSteps,
    out vec4 color, out vec3 normal, out int returnCode
){
    dist = 0.;
    float prevDist = dist;
    numSteps = 0;
    color = vec4(0);
    vec3 prevPos = pos;
    float prevSd;
    float sd = 0.;

    //
    const float res = 2.;

    // Trace.
    for(int i=0; i<maxNumSteps; i++) {
        //
        numSteps++;

        // Calc sd.
        prevSd = sd;

        //float sdSphere = sdSphere(/*in vec3 pos=*/pos, /*in vec3 center=*/vec3(0), /*in float radius=*/1.);
        //float sdDensity = sdDensity(/*in vec3 pos=*/pos, /*in float resolution=*/res);
        float sdCave = sdCave(/*in vec3 pos=*/pos, /*in float resolution=*/res);

        //sd = sdDensity;
        sd = sdCave;

        if(dist + sd > maxDist){
        	sd = maxDist-dist;
            dist += sd;
            pos += dir*sd;

            color = vec4(0);
            normal = vec3(0);

            returnCode = TOO_FAR;
        	return;
        }
        if(sd <= minDist + dist*0.001){

            //if(i==2) DEBUG_MARKER = 1.;
            //if(sd < 0.0) DEBUG_MARKER = 1.;

            // Linearly interpolate position.
            float fac = (minDist + dist*0.001 - sd) / (prevSd - sd);
            pos = mix(pos, prevPos, fac);

            //sdDensityNormal(
            //    /*in vec3 pos=*/pos, /*float diff=*/max(dist*3./iResolution.x, 0.002), /*float resolution=*/res+1.,
            //    /*inout vec3 normal=*/normal, /*inout float sd=*/sd
            //);
            sdCaveNormal(
                /*in vec3 pos=*/pos, /*float diff=*/max(dist*3./iResolution.x, 0.002), /*float resolution=*/res+0.5,
                /*inout vec3 normal=*/normal, /*inout float sd=*/sd
            );

            color = vec4(155., 155., 154., 0.)/255.;

            float f = clamp(getLayeredDensityBufC(pos*5., 1.)*20.-5., 0., 1.);
            color = mix(color, vec4(255, 215, 0, 32)/255., f);
            //color = mix(color, vec4(100, 255, 0, 92)/255., f);

            vec3 towardsMiddle;
            float sdTowardsMiddle;
            //sdCaveCenterNormal(pos, 0.004, /*out vec3 normal=*/towardsMiddle, /*inout float sd=*/sd);
            sdCaveGradientNormal(
                /*in vec3 pos=*/pos, /*float diff=*/0.1,
                /*inout vec3 normal=*/towardsMiddle, /*inout float sd=*/sdTowardsMiddle
            );
            towardsMiddle = normalize(-towardsMiddle);
            float grassFactor = min(pow(dot(towardsMiddle, normal)+0.1, 8.), 1.);
            color = mix(color, vec4(146., 116., 32., 0)/255., grassFactor);

            //float noise = getLayeredDensityBufC(pos, 1.);
            //color.r += noise*1.;

            returnCode = INTERSECTED;
            return;

            //if(sd < 0.){
            //	color = vec3(1,0,0);
            //}

            //color=vec3(1,1,0);


        }

        //
        prevDist = dist;
        dist += sd;
        prevPos = pos;
        pos += dir*sd;

    }
    returnCode = TOO_MANY_STEPS;
    return;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 cameraPos = texelFetch(iChannel0, ivec2(CAMERA_POS, 0), 0).xyz;
    vec3 forward = normalize(texelFetch(iChannel0, ivec2(CAMERA_DIRECTION_FORWARD, 0), 0).xyz);
    vec3 right = normalize(texelFetch(iChannel0, ivec2(CAMERA_DIRECTION_RIGHT, 0), 0).xyz);
    vec3 up = cross(right, forward);

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;
    float aspectRatio = iResolution.x / iResolution.y;

    vec3 camPos = vec3(cameraPos.x, cameraPos.y, cameraPos.z);
    vec3 viewDir = normalize(forward*0.3 + right * uv.x + up * uv.y);

    // Light cone.
    float coneFactor = max(pow(min(dot(viewDir, forward) + 0.15, 1.), 16.), 0.);
    coneFactor = (coneFactor*0.6 + 0.4);

    //
    vec3 pos = camPos;
    vec3 dir = viewDir;
    float dist = 0.;
    float maxDist = 50.;
    float minDist = 0.002;
    int numSteps;
    int maxNumSteps = int(75. * coneFactor);
    vec4 color;
    vec3 normal;
    int returnCode;
    marchWorld(
        /*inout vec3 pos=*/pos, /*inout vec3 dir=*/dir,
        /*out float dist=*/dist, /*in float maxDist=*/maxDist, /*in float minDist=*/minDist,
        /*out int numSteps=*/numSteps, /*in int maxNumSteps=*/maxNumSteps,
        /*out vec3 color=*/color, /*out vec3 normal=*/normal, /*out int returnCode=*/returnCode
    );

    //vec3 lightDir = normalize(vec3(5.,-1,3.));
    vec3 lightDir = viewDir;

    // Ambient occlusion.
    vec3 occlusionColor = vec3(0.,0.,0.);
    float occlusionFactor = float(numSteps)/log(dist+10.) * max(dot(-viewDir, normal), 0.);
    occlusionFactor = max(1.-occlusionFactor*0.025, 0.);

    float diff = max(dot(-lightDir, normal), 0.)*0.75;

    vec3 reflectDir = reflect(lightDir, normal); // reflect(I, N) = I - 2.0 * dot(N, I) * N.
    float spec = pow(max(dot(reflectDir, -viewDir), 0.0), 8.) * 0.25;

    float ambient = 0.125*occlusionFactor;

    color = mix(
        (color*diff + color*spec)*coneFactor + color*ambient,
        color, color.w
    );

    // Mist.
    //vec3 mistColor = vec3(0.5,0.6,0.9);
    vec4 mistColor = vec4(0., 0., 0., 0);
    float mistFactor = max(float(numSteps)/float(maxNumSteps), float(dist)/float(maxDist));
    mistFactor = pow(mistFactor, 1.);
    color = mix(color, mistColor, mistFactor);

    // The sun.
    float d = dist/maxDist * 2.*max(mistFactor*mistFactor*mistFactor-0.5, 0.)*min(pow(max(dot(dir, -lightDir), 0.), 1024.), 1.);
    color = min(color + vec4(d), 1.);

    //color.r = float(numSteps);
    //if(numSteps == 3){
    //	color.r = 1.;
    //}
    //color.r += DEBUG_MARKER;
    if(DEBUG_COLOR_MARKER != vec3(-1)){
    	//color += DEBUG_COLOR_MARKER;
    }

    // Output to screen
    fragColor = vec4(color.xyz,1.0);
}

