const float shiftExponent = 4096.;
const int shiftNum = 12;
const int shiftMask = 0xfff;
float pack(float v1, float v2){
    return floor(clamp(v1, 0., 1.)*shiftExponent) + floor(clamp(v2, 0., 1.)*shiftExponent)*shiftExponent;
}
void unPack(in float data, out float v1, out float v2){
    v1 = float(int(data) & shiftMask)/shiftExponent;
    v2 = float((int(data) >> shiftNum) & shiftMask)/shiftExponent;
}

