float getDensityCave(in vec3 pos){
    float densityA = getDensity(pos, 0, 1);
    float densityB = getDensity(pos, 1, 1);
    float densityC = getDensity(pos, 2, 1);

    float density;
    if(densityA > densityC){
    	density = max(densityA, densityB) - min(densityC, densityB);
    } else {
    	density = max(densityC, densityB) - min(densityA, densityB);
    }

    return density;
}
