const float ParticleDensity = 1.; // 0.0-1.0

float sensorLen = 0.025;
float sensorAng = 0.5;
float rotAng = 0.5;

mat2 rotate(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


vec2 sense (vec2 _uv, vec2 _pDir)
{

    //float r = plot(uv,cPos.xy,size);   //Draws location of part

    vec2 sensorL = _uv+(rotate(-sensorAng)*_pDir)*sensorLen; // location of sensor A
    vec2 sensorC = _uv+_pDir*sensorLen;
    vec2 sensorR = _uv+(rotate(sensorAng)*_pDir)*sensorLen;

    float senseL = texture(iChannel1, sensorL).a;
    float senseC = texture(iChannel1, sensorC).a;
    float senseR = texture(iChannel1, sensorR).a;

    if(senseC > senseL && senseC > senseR)
    {

    }

    else if (senseC < senseL && senseC < senseR)
    {
      float randNum = texture(iChannel3, vec2(0.5,sin(iTime))).x; //add uv to the sin to get a better random
      randNum = round(randNum)*2.-1.;
      _pDir *= rotate(rotAng*randNum);
      // _pDir = vec2(-0.5,-0.5);// for debug
    }


    else 
    {
        _pDir *= rotate(rotAng*sign(senseL-senseR));
    }

    return _pDir;

}



// hash without sine
// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p) {
    vec3 MOD3 = vec3(443.8975, 397.2973, 491.1871);
    vec3 p3 = fract(vec3(p.xyx) * MOD3);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 res = iResolution.xy;
    vec2 px = 1. / res;
    vec2 uv = fragCoord.xy / res;

    vec4 buf[9];
    buf[0] = texture(iChannel0, uv);
    buf[1] = texture(iChannel0, fract(uv-vec2(px.x, 0.)));
    buf[2] = texture(iChannel0, fract(uv-vec2(-px.x, 0.)));
    buf[3] = texture(iChannel0, fract(uv-vec2(0., px.y)));
    buf[4] = texture(iChannel0, fract(uv-vec2(0., -px.y)));
    buf[5] = texture(iChannel0, fract(uv-vec2(px.x, px.y)));
    buf[6] = texture(iChannel0, fract(uv-vec2(-px.x, px.y)));
    buf[7] = texture(iChannel0, fract(uv-vec2(px.x, -px.y)));
    buf[8] = texture(iChannel0, fract(uv-vec2(-px.x, -px.y)));

    // this cell's particle direction & position, if any
    vec2 pDir = buf[0].rg;
    vec2 pPos = buf[0].ba;


    // update this cell's particle position
    pPos = mod(pPos+pDir, res);


    // clear the current cell if its particle leaves it
    if(floor(pPos)!=floor(fragCoord)) {
        pDir = vec2(0.);
        pPos = vec2(0.);
    }

    // add up any incoming particles
    float ct = 0.;
    vec2 pDirAdd = vec2(0.);
    vec2 pPosAdd = vec2(0.);
    for(int i=1; i<9; i++) {
        vec2 pPosI = buf[i].ba;
        pPosI = mod(pPosI+buf[i].rg, res);
        if(floor(pPosI)==floor(fragCoord)) {
            pDirAdd += buf[i].rg;
            pPosAdd += pPosI;
            ct ++;
        }
    }

    // if particles were added up, average and transfer them to the current cell
    if(ct>0.) {
        pDir = normalize(pDirAdd / ct);




        //pDir = mix(pDir, texture(iChannel1, uv).rg*2.-1., 0.1 );  //trail follow

        pDir = sense(uv,pDir); //direction follow


        pPos = pPosAdd / ct;

        if(texture(iChannel2, vec2(32./256., .5)).r>.5){
            pDir = vec2(
                    hash12(mod(uv+iTime/100.-4., 100.)),
            		hash12(mod(uv-iTime/100.-8., 100.))
				)-.5;
        }

        if(texture(iChannel2, vec2(37./256., .5)).r>.5){
            pDir = vec2(-1., 0.);
        }
        if(texture(iChannel2, vec2(39./256., .5)).r>.5){
            pDir = vec2(1., 0.);
        }
        if(texture(iChannel2, vec2(38./256., .5)).r>.5){
            pDir = vec2(0., 1.);
        }
        if(texture(iChannel2, vec2(40./256., .5)).r>.5){
            pDir = vec2(0., -1.);
        }
    }

    // first frame particle setup
    if(iFrame==0 || (iMouse.z >0. && distance(iMouse.xy, fragCoord)<20.) )
        if(ParticleDensity>hash12(fragCoord/res)) {
            vec2 randXY =
                vec2(
                    hash12(mod(uv+iTime/100.-4., 100.)),
            		hash12(mod(uv-iTime/100.-8., 100.))
				);

            pDir = normalize(randXY-.5);
            pPos = fragCoord;
        }

    fragColor = vec4(pDir, pPos);
}

