    h.x=min(h.x,length(p.xy)-.15+abs(p.z)*.1);
    t=t.x<h.x?t:h;
    return t;
}
vec2 mp( vec3 p )
{   
    op=modP=p; //SCENE 1 ANGULAR SCENE
    mixer=clamp(sin(tt*.65+p.z*.1),-.5,.5)+.5; //OVERALL SCENE MIXER 
    modP.z=mod(modP.z+tt*2.,40.)-20.; 
    np=modP; 
    for(int i=0;i<5;i++){
        np.xz=abs(np.xz)-vec2(3.5,2);
        np.xz*=r2(.785);
        np.yz*=r2(-sin(-p.y*.1 )*.1);
    }  
    vec2 h,scene1=fbAngular(np);  
    np.xz+=1.2;
    np.xy*=r2(.785*2.);  
    h=fbAngular(np*1.5); h.x/=1.5;
    scene1=scene1.x<h.x?scene1:h;  
    np.xz*=r2(-.785*2.);
    np.x+=4.;
    h=fbAngular(abs(np*.5)-vec3(0,0,5)); h.x/=.5;
    scene1=scene1.x<h.x?scene1:h;
    scene1.x*=0.8;  //SCENE 2 ORGANIC SCENE    
    float tnoi=texNoise((p.xz+vec2(0,tt*2.))*.02).r*1.5; 
    float tDisp=sin((p.z+tt*2.)*.5+p.x*.2);
    p.y+=tDisp;
    vec2 scene2 =vec2(p.y+5.+tnoi*5.,1.); //TERRAIN  
    scene2.x=smin(scene2.x,length(abs(modP.xz+tnoi*2.)-vec2(3.5,2))-2.+tnoi*2.+sin(p.y*.3)+sin(p.y*2.*p.x)*0.03,5.); //VERTICAL CYLINDERS
    vec3 cylP=modP+vec3(0,5,0); cylP.x=abs(cylP.x)-8.+tDisp;
    scene2.x=smin(scene2.x,length(cylP.xy+tnoi*2.)-2.+tnoi*2.+sin(p.y*.3)+sin(p.y*2.*p.x)*0.017,5.);//HORIZONTAL CYLINDERS
    scene2.x=smax(length(modP+vec3(0,6,0)+tnoi*3.)-4.,scene2.x,5.);  //HOLE IN MIDDLE OF TERRAIN
    //scene2.x=smax(length(modP+vec3(0,12,0))-3.4+tnoi,scene2.x,2.); //Dig hole where blue sphere with tentacle is, removed to optimize shader as design impact is minimal
    scene2.x*=0.5;
    h=vec2(length(np.yz+vec2(-.3,0.)),6);
    h.x=min(h.x,max(length(cos(p*.5+sin(op.z*.2)+vec3(0,tt,tt))),abs(p.x)-3.));
    modP.xz*=r2(sin(p.y*.2+tt));  
    h.x=min(h.x,length(abs(modP.xz)-.5)-max(0.,.5-abs(p.y+10.)*.1));  
    h.x=smin(h.x,length(modP+vec3(0,12,0))-3.+tnoi,1.5);
    scene2.x=min(scene2.x,h.x);  
    g+=0.1/(0.1+h.x*h.x*(40.-39.*sin(op.z*.7)));  
    vec2 t=mix(scene1,scene2,vec2(mixer,0.));  
    return t;
}
vec2 tr( vec3 ro, vec3 rd )
{
    vec2 h,t=vec2(.1);
    for(int i=0;i<128;i++){
        h=mp(ro+rd*t.x);
        if(h.x<.0001||t.x>60.) break;
        t.x+=h.x;t.y=h.y;
    }
    if(t.x>60.) t.y=0.;  
    return t;
}
#define a(d) clamp(mp(po+no*d).x/d,0.,1.)
#define s(d) smoothstep(0.,1.,mp(po+ld*d).x/d)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord.xy/iResolution.xy-0.5)/vec2(iResolution.y/iResolution.x,1);
    tt=23.6+mod(iTime,62.82);
    vec3 ro=mix(vec3(12.,5.,-9.),vec3(0.,-2.,-10.),ceil(sin(tt*.4))),
         cw=normalize(vec3(0,-2.,0)-ro),cu=normalize(cross(cw,vec3(0,1,0))),cv=normalize(cross(cu,cw)),
         rd=mat3(cu,cv,cw)*normalize(vec3(uv,.5)),co,fo;
    z=tr(ro,rd);t=z.x;  
    float timeMixer=clamp(sin(tt*.65),-.5,.5)+.5;
    ld=normalize(vec3(.3,.2,.5));
    vec3 sun=vec3(pow(clamp(dot(ld,rd),0.,1.),32.))*mix(vec3(.7,.4,0.),vec3(0.2),timeMixer);  
    co=fo=sun+(mix(vec3(.6,.3,.3),vec3(.1,.15,.2),timeMixer)-length(uv)*.1-rd.y*.2);
    if(z.y>0.){ 
        po=ro+rd*t; 
        no=normalize(e.xyy*mp(po+e.xyy).x+e.yyx*mp(po+e.yyx).x+e.yxy*mp(po+e.yxy).x+e.xxx*mp(po+e.xxx).x);
        al=vec3(1);
        if(z.y>=1.) al=mix(vec3(.1,.2,.4),vec3(1),2.-z.y);
        if(z.y>=3.) al=mix(vec3(0),vec3(1),4.-z.y);   
        float dif=max(0.0,dot(no,ld)),    
              fr=pow(1.+dot(no,rd),4.),    
              spo=exp2(1.+25.*texNoise(.05*vec2(np.y,dot(np.xz,vec2(.7)))).r),
              sp=pow(max(dot(reflect(-ld,no),-rd),0.),spo);
        co=mix(sp*.5+al*(a(.05)*a(.1)+.2)*(dif+s(2.)),fo,min(fr,.5));
        co=mix(fo,co,exp(-.00005*t*t*t));
    }
    fragColor = vec4(pow(co+g*.2*mix(vec3(.7,.2,.1),vec3(.1,.2,.7),timeMixer),vec3(.45)),1);
}

