        M = density_target + pos.x/R.x*0.01 + pos.y/R.x*0.01;
    }

    X = clamp(X - pos, vec2(-0.5), vec2(0.5));
    U = vec4(ENCODE(X), ENCODE(V), M, 0.);
}

