// Two versions of a spectrum function: one using smoothsteps and the other
// using best-fit 9th degree polynomials which isn't as accurate, but should
// be significantly faster. 
// Function inputs range from 0 to 1, 
// which corresponds to roughly 380 to 720 nanometers
//
// I figured perhaps it could be useful to someone!

#define S smoothstep

vec3 Spectrum(float x) {
    // https://www.shadertoy.com/view/wlSBzD
	float r, g, b;

    r = x<.16 ? S(0., .16, x)*.169 :
    	x<.22 ? S(.22, .16, x)*.134+.035 :
    	x<.41 ? S(.22, .41, x)*.098+.035 :
    	x<.64 ? S(.41,.64,x)*.851+.133 :
    			S(1., .64, x)*.984;

    g = x<.05 ? 0. :
    	x<.15 ? S(.05, .15, x)*.047 :
    	x<.45 ? S(.15, .45, x)*.882+.047 :
    	x<.70 ? S(.70, .45, x)*.796+.133 :
    			S(1.0, .70, x)*.133;

    b = x<.18 ? S(0.0, .18, x)*.5 :
    	x<.22 ? S(.22, .18, x)*.1+.4 :
    	x<.35 ? S(.22, .35, x)*.059+.4 :
    	x<.54 ? S(.54, .35, x)*.334+.125 :
    	x<.60 ? S(.54, .60, x)*.169+.125 :
    	x<.69 ? S(.69, .60, x)*.243+.051 :
    	x<.72 ? S(.69, .72, x)*.043+.051 :
    	x<.89 ? S(.89, .72, x)*.094 : 0.;

    return vec3(r,g,b);
}

vec3 SpectrumPoly(float x) {
    // https://www.shadertoy.com/view/wlSBzD
    return (vec3( 1.220023e0,-1.933277e0, 1.623776e0)
          +(vec3(-2.965000e1, 6.806567e1,-3.606269e1)
          +(vec3( 5.451365e2,-7.921759e2, 6.966892e2)
          +(vec3(-4.121053e3, 4.432167e3,-4.463157e3)
          +(vec3( 1.501655e4,-1.264621e4, 1.375260e4)
          +(vec3(-2.904744e4, 1.969591e4,-2.330431e4)
          +(vec3( 3.068214e4,-1.698411e4, 2.229810e4)
          +(vec3(-1.675434e4, 7.594470e3,-1.131826e4)
          + vec3( 3.707437e3,-1.366175e3, 2.372779e3)
            *x)*x)*x)*x)*x)*x)*x)*x)*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0);
    vec3 spectrum = mod(iTime, 2.)<1. ? Spectrum(uv.x) : SpectrumPoly(uv.x);

    col = spectrum*S(.3, .7, uv.y);
    col += S(.01, .0, abs(spectrum-uv.y*2.));

    fragColor = vec4(col,1.0);
}

float Circle(vec2 uv, vec2 p, float r, float blur) {
	float l = length(uv-p);
    float f = smoothstep(r,r-blur, l);
    return f;
}

vec3 Smiley(vec2 uv, vec2 p, float size, float blur) {
    uv -= p;
    uv/=size;
    float f = Circle(uv, vec2(0.,0.), 0.5, blur);
    float eye = Circle(uv, vec2(0.2,0.2), 0.1, blur);
	f -= eye;
    float eye2 = Circle(uv, vec2(-0.2,0.2), 0.1, blur);
    f -= eye2;

    float mouth = Circle(uv, vec2(0.,0.01), 0.4, blur);
    mouth -= Circle(uv, vec2(0.,0.12), 0.4, blur);
    f -= mouth;
    vec3 color = vec3(1.0, 1.0, 0.)*f;
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
	uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
   	 		log(iSampleRate);
    uv.x += sin(iTime);
    float x = uv.x;
    float m=0.;
   // m = sin(iTime+x*8.)*.1;
    float y = uv.y-m;
    float blur =0.01;
   // blur =abs(sin(uv.x+iTime))*0.4;
    // Time varying pixel color
    vec3 f = vec3(0.);
    f+= Smiley(vec2(x,y), vec2(0.), 0.8, blur); 

    fragColor = vec4(f,1.0);

}

// Sun settings.
const float sunIntensity = 1.0;

// Planet settings.
const vec3 planetPosition = vec3(0.0, 0.0, 3.0);
const float planetRadius = 1.5;
const vec3 planetColor = vec3(0.8);

// Atmosphere settings.
const float atmosphereSize = 0.2;
const float densityFalloff = 3.0;
const vec3 wavelengths = vec3(750.0, 530.0, 400.0);
const float scatteringStrength = 15.0;

// Atmosphere calculations.
const float atmosphereRadius = planetRadius + atmosphereSize;
const vec3 scatteringCoefficients = pow(400.0 / wavelengths, vec3(4.0)) * scatteringStrength;

// Quality settings.
const int inScatterPoints = 20;
const int outScatterPoints = 20;

// Globals.
vec3 sunDirection;

// Intersection of a ray with a sphere. Returns both intersection times as a
// vector where x is the nearest intersection and y is the farthest
// intersection.
vec2 rayIntersectSphere(in vec3 ro, in vec3 rd, in vec3 ce, float ra) {
	vec3 oc = ro - ce;
	float b = dot(oc, rd);
	float c = dot(oc, oc) - ra * ra;
	float h = b * b - c;
	if (h < 0.0) {
		return vec2(-1.0);
	}
	h = sqrt(h);
	return vec2(-b - h, -b + h);
}

// The density at a position above the surface but below the atmosphere's edge.
float densityAtPoint(in vec3 p) {
	// Get the elevation of the point above the surface of the planet.
	float elevation = length(p - planetPosition) - planetRadius;

	// Scale the elevation so that a point directly on the surface of the
	// planet would have a value of 0, and a point directly on the edge of the
	// atmosphere would have a value of 1.
	float elevationScalar = elevation / atmosphereSize;

	// Use some math to determine the density.
	return exp(-elevationScalar * densityFalloff) * (1.0 - elevationScalar);
}

// Calculate the average density of the atmosphere along a given finite ray
// multiplied by the ray's length. This is referred to as the optical depth of
// the ray.
float opticalDepth(in vec3 ro, in vec3 rd, float rl) {
	vec3 p = ro;
	float dt = rl / float(outScatterPoints - 1);
	float accumulatedDensity = 0.0;
	for (int i = 0; i < outScatterPoints; i++) {
		accumulatedDensity += densityAtPoint(p) * dt;
		p += rd * dt;
	}
	return accumulatedDensity;
}

// Accumulated light along a given finite ray (referred to as the light-ray).
vec3 accumulateLight(in vec3 ro, in vec3 rd, float rl) {
	vec3 p = ro;
	float dt = rl / float(inScatterPoints - 1);
	vec3 accumulatedLight = vec3(0.0);
	for (int i = 0; i < inScatterPoints; i++) {
		// Cast a ray from the current point to the sun. Store the length of
		// this ray.
		float sunRayLength = rayIntersectSphere(p, sunDirection, planetPosition, atmosphereRadius).y;

		// Calculate the optical depth of the sun-ray.
		float sunRayOpticalDepth = opticalDepth(p, sunDirection, sunRayLength);

		// Calculate the optical depth of the ray from the current point to the
		// origin of the light-ray.
		float viewRayOpticalDepth = opticalDepth(p, -rd, dt * float(i));

		// Use some math to calculate the transmission.
		vec3 transmission = exp(-(sunRayOpticalDepth + viewRayOpticalDepth) * scatteringCoefficients);

		// Get the density at the current point.
		float density = densityAtPoint(p);

		// Accumulate light.
		accumulatedLight += density * transmission * scatteringCoefficients * dt;
		p += rd * dt;
	}

	return accumulatedLight;
}

