// }

// Main image.
void mainImage(out vec4 rgba, in vec2 fragCoord) {
    // Use the mouse to determine the sun's position.
	sunDirection = normalize(vec3((2.0 * iMouse.xy - iResolution.xy) / iResolution.y * 5.0, 1.0));

    // Set up the camera.
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 ro = vec3(0.0, 0.0, -3.0);
	vec3 rd = normalize(vec3(uv, 2.0));

    // The color of this fragment.
	vec3 rgb;

	// Raycast to the planet.
	vec2 planetIntersection = rayIntersectSphere(ro, rd, planetPosition, planetRadius);
	if (planetIntersection.x > 0.0) {
		// The planet was hit by the ray. Calculate the normal.
		vec3 p = ro + rd * planetIntersection.x;
		vec3 n = normalize(p - planetPosition);

		// Diffuse lighting.
		float diffuse = max(0., dot(n, sunDirection)) * sunIntensity;

        // Coloring.
		rgb = vec3(planetColor * diffuse);	 
	} else {
		// The planet was not hit by the ray. Do sun lighting.
        float angle = max(0.0, dot(sunDirection, rd));
        float sun = pow(angle, 150.0);
        float aura = pow(angle, 800.0);
		rgb = vec3(sun + aura);
	}

    // Raycast to the atmosphere.
	vec2 atmosphereIntersection = rayIntersectSphere(ro, rd, planetPosition, atmosphereRadius);
	if (atmosphereIntersection.x > 0.) {
	  	// Calculate the distance passed through the atmosphere by the ray,
		// taking into account the planet (which may obstruct the ray).
		float distanceThroughAtmosphere;
		if (planetIntersection.x > 0.0) {
			// Hits the planet.
			distanceThroughAtmosphere = planetIntersection.x - atmosphereIntersection.x;
		} else {
			// Does not hit the planet.
			distanceThroughAtmosphere = atmosphereIntersection.y - atmosphereIntersection.x;
		}

        // Accumulate light along the finite ray starting where the ray first
        // interescted with the atmosphere and ending where the ray either
        // intersected the planet or exited the atmosphere.
		vec3 p = ro + rd * atmosphereIntersection.x;
		vec3 light = accumulateLight(p, rd, distanceThroughAtmosphere) * 1.2;

        // Mix the lighting with the color.
		rgb = rgb * (1.0 - light) + light;
	}

    // Output the color.
	rgba = vec4(rgb, 1.);
}

