float N21( vec2 p ) {
   //float c = fract( sin (p.x*100.0 + p.y * 6574.0) * 5647.0 );

   float c = fract( sin (p.x*190.0 + p.y * 65.0) * 56.0 );
   return c;
}

float SmoothNoise( vec2 uv ) {

    vec2 lv = fract(uv ); // level
    //lv = smoothstep( 0.0 , 1.0 , lv );		// smoothstep lv
    lv = lv * lv * ( 3.0 - 2.0 * lv );

    vec2 id = floor(uv ); // id


    float bl = N21( id );
    float br = N21( id + vec2( 1 , 0 ) );
    float b = mix( bl , br , lv.x );


    float tl = N21( id + vec2( 0 , 1 ));
    float tr = N21( id + vec2( 1 , 1 ));
    float t = mix( tl , tr , lv.x );


    float c = mix( b , t , lv.y );


    return c;
}


float SmoothNoise2( vec2 uv ) {
  	float c = 0.0;

    c += SmoothNoise( uv * 4.0  ) * 1.0;
    c += SmoothNoise( uv * 8.0  ) * 0.5;
    c += SmoothNoise( uv * 16.0 ) * 0.25;
    c += SmoothNoise( uv * 32.0 ) * 0.125;
    c += SmoothNoise( uv * 64.0 ) * 0.0625;

    c /= 2.0;   
    return c;
}



