const bool useMouse = true;


//All df function taken from the df master iq
//https://iquilezles.org/articles/distfunctions
float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float opUnion( float d1, float d2 ) {  return min(d1,d2); }

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

float opIntersection( float d1, float d2 ) { return max(d1,d2); }

float displacement(float d1, vec3 p, sampler2D channel, float strength, float tile)
{
    float d2 = texture(iChannel0, p.xy*tile).x * strength;
    return d1+d2;
}

float scene(vec2 uv) {
    vec3 uvs = vec3(uv,0.);
    vec3 sphereuvs = vec3(uv - uvoffset,0.);
    if(useMouse){
    	vec2 mouseuv = iMouse.xy/iResolution.yy - vec2((iResolution.x/iResolution.y)/2.,0.5);
        sphereuvs = vec3(uv - mouseuv,0.);
    }
	float scene = 0.;
    float sphere = sdSphere(sphereuvs, maxsize*(sin(iTime*speed)/2.+0.5));
    float sphere2 = sdSphere(sphereuvs, maxsize*(sin(iTime*speed)/2.+0.5) + offset);
    float box = sdRoundBox(uvs, vec3(0.79,0.41,1.0), 0.05);
    if(useDisplacement){
    	sphere2 = displacement(sphere2, uvs, iChannel0, 0.1, 0.7);
    	sphere = displacement(sphere, uvs, iChannel0, 0.1, 0.7);
        box = displacement(box, uvs, iChannel0, 0.04, 01.2);
    }
    scene = sphere;
    scene = opIntersection(scene,box);
    scene = opSubtraction(scene,sphere2);
	return scene;
}


