#define tt 0.1 * t

#define EPS 0.001
#define STEPS 128.0
#define DIST 0.01

#define S(a, b, t) smoothstep(a, b, t)

float map(float n, float start1, float stop1, float start2, float stop2) {
  return (n - start1) / (stop1 - start1) * (stop2 - start2) + start2;
}

float map01(float n, float start2, float stop2) {
  return (0.5 * n + 0.5) * (stop2 - start2) + start2;
}

vec2 rotate2d(vec2 uv, float a) {
  float c = cos(a);
  float s = sin(a);
  return mat2(c, -s, s, c) * uv;
}

float circleSDF(vec3 p, float r) {
  return length(p) - r;
}

float gyroidSDF(vec3 p, float s, float th) {
  p *= s;
  return abs(dot(sin(t + p), cos(-t + p.zxy))) / s - th;
}

float sceneSDF(vec3 p) {
  vec3 _p = p;

  float circle = circleSDF(p, 3.0);

  p.y += t + cos(t);
  p.z += t + sin(t);
  float gyroid = gyroidSDF(p, 2.5, 0.025);

  return max(circle, 0.5 * gyroid);
}

vec3 computeNormal(vec3 p) {
  float center = sceneSDF(p);
  vec3 offset = vec3(0.0, EPS, 0.0);

  return normalize(vec3(
    center - sceneSDF(p + offset.yxx),
    center - sceneSDF(p + offset.xyx),
    center - sceneSDF(p + offset.xxy)
  ));
}

vec3 computeSurface(vec3 pos, float f) {
  vec3 normal = computeNormal(pos);

  vec3 lightPos = vec3(1.0, 2.0, 3.0);
  vec3 lightColor = vec3(0.3922, 0.6157, 0.8275);
  float light = max(0.0, dot(normal, lightPos));

  vec3 ambient = vec3(0.1255, 0.0039, 0.0667);

  float fog = 5.0 / pow(f, 2.0);

  return fog * light * lightColor + ambient;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

  vec3 color = vec3(0.8235, 0.9608, 0.9373);

  vec3 rd = normalize((vec3(uv, 1.0)));
  vec3 ro = vec3(0.0, 0.0, -7.5);

  float angle = -0.5 * QP;
  rd.yz = rotate2d(rd.yz, angle);
  ro.yz = rotate2d(ro.yz, angle);

  float f = 10.0 / STEPS;
  for (float i = 0.0; i < STEPS; i++) {
    vec3 pos = ro + f * rd;
    float scene = sceneSDF(pos);
    if (scene < DIST) {
      color = computeSurface(pos, f);
      break;
    }
    f += scene;
  }

  fragColor = vec4(pow(color, vec3(1.0/2.2)), 1.0);
}

