vec3 cameraPosition = vec3(0.0);
mat3 cameraMatrix = mat3(1, 0, 0, 0, 1, 0, 0, 0, 1);
float distanceEstimator(vec3 z) {
    vec3 oz = mod(z, 4.0) - 2.0;
    return length(oz - clamp(oz, -1.0, 1.0)) - .2;
}

vec3 xDir = vec3(0.0001, 0, 0);
vec3 yDir = vec3(0, 0.0001, 0);
vec3 zDir = vec3(0, 0, 0.0001);
vec3 lightDirection = normalize(vec3(0.5, 0.5, -1.0));

vec3 surfaceNormal (vec3 pos) {
    vec3 normal = vec3(
        distanceEstimator(pos + xDir) - distanceEstimator(pos - xDir),
        distanceEstimator(pos + yDir) - distanceEstimator(pos - yDir),
        distanceEstimator(pos + zDir) - distanceEstimator(pos - zDir)
    );
    return normalize(normal);
}

vec3 trace (vec3 origin, vec3 direction) {
    float totalDistance = 0.0;

    for (float steps = 0.0; steps < 400.0; steps ++) {
        vec3 pos = origin + direction * totalDistance;
        float distance = distanceEstimator(pos);
        totalDistance += distance;
        if (distance < 0.001) {
            vec3 normal = surfaceNormal(pos);
            float diffuse = max(dot(normal, lightDirection), 0.0);
            float specular = dot(reflect(direction, normal), lightDirection);

            vec3 texPos = (mod(pos, 4.0) - 2.0);

            vec2 texPos2 = texPos.xy;

            if (abs(normal.x) > abs(normal.y) && abs(normal.x) > abs(normal.z)) {
            	texPos2 = texPos.yz;
            } else if (abs(normal.y) > abs(normal.z)) {
            	texPos2 = texPos.xz;
            } else {
            	texPos2 = texPos.xy;
            }

            specular = pow(max(specular, 0.0), 200.0);

            float shade = 0.6 * diffuse + 0.4 * specular + 0.1;            
            vec3 tint = texture(iChannel1, texPos2).rgb;
            float m = mod(floor(pos.x/4.0) + floor(pos.y/4.0) + floor(pos.z/4.0), 4.0);
            if (m == 0.0) {
            	tint = texture(iChannel0, texPos2).rgb;
            } else if (m == 1.0) {
            	tint = texture(iChannel1, texPos2).rgb;
            } else if (m == 2.0) {
            	tint = texture(iChannel2, texPos2).rgb;
            } else {
            	tint = texture(iChannel3, texPos2).rgb;
            }
            return shade * tint;


        }
    }
    return 0.2 * vec3(232.0, 159.0, 14.0) / 255.0;
}



