vec3 interpool(vec3 p0, vec3 p1, vec3 p2, float t) {
    t = clamp(t, 0., 1.);
    float oneMinusT = 1. - t;

    return
        oneMinusT * oneMinusT * p0 +
        2. * oneMinusT * t * p1 +
        t * t * p2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 px = gl_FragCoord.xy / iResolution.xy;

    const int size = 32;

    const float inv = 1. / float(size);

    float sfx[size];   

    vec3 colors[size];

    for (int i = 0; i < size; ++i) {

        float scale = 0.75;

        sfx[i] = texelFetch(iChannel0, ivec2(int(float(i)/float(size)*512.),0), 0).x * scale;
        //sfx[i] = texture(iChannel0, vec2(float(i)*inv, 0)).x * scale;

        /*colors[i] = vec3(cos(1. - px.x + iTime) * 0.5 +0.5, 
                         1. - cos(iTime)*sin(iTime)* 0.5 +0.5, 
                         sin(1. - px.x + iTime) * 0.5 +0.5);*/

        colors[i] = interpool(vec3(0,0,1), vec3(0.2,1,0.2), vec3(1,0.2,0), sfx[i]/0.75);
    }



	int index = int(px.x / inv);

    float height = sfx[index];

    if (px.y > height || mod(px.x, inv) < 0.008 || mod(px.y, inv) < 0.008) {
    	fragColor = vec4(vec3(0,0,0), 1);    
    }
    else {
        //Use % to hide first and last bar
        fragColor = vec4(colors[index], 1) * float((bool(index % (size-1))));
    }

    if (abs(texelFetch(iChannel0, ivec2(int(px.x*512.),1), 0).x * 0.25 - (px.y - 0.75)) < 0.01) {
        fragColor.xyz += vec3(1);   
    }
}

