#define MAX_STEPS 100
#define MAX_DIST 100.
#define EPSILON 0.0001
#define PI 3.14159265
#define RAY 1.
#define BLUE 2.
#define BLACK 3.
#define BG 4.


mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
	return mat2(c, -s, s, c);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
} // TODO replace with Rot

float sdSphere(vec3 p, float radius) { return length(p) - radius; }
float sdBox( vec3 p, vec3 b ) { vec3 q = abs(p) - b; return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0); }
float rnd( float x ) { return fract(10000. * sin(x * 1000.));}

float sdTorus(vec3 p, float smallRadius, float largeRadius) {
    return length(vec2(length(p.xz) - largeRadius, p.y)) - smallRadius;
}


vec3 IcosSym (vec3 p)
{
  float dihedIcos = 0.5 * acos (sqrt (5.) / 3.);
  float a, w;
  w = 2. * PI / 3.;
  p.z = abs (p.z);
  p.yz = Rot2D (p.yz, - dihedIcos);
  p.x = - abs (p.x);
  for (int k = 0; k < 4; k ++) {
    p.zy = Rot2D (p.zy, - dihedIcos);
    p.y = - abs (p.y);
    p.zy = Rot2D (p.zy, dihedIcos);
    if (k < 3) p.xy = Rot2D (p.xy, - w);
  }
  p.z = - p.z;
  a = mod (atan (p.x, p.y) + 0.5 * w, w) - 0.5 * w;
  p.yx = vec2 (cos (a), sin (a)) * length (p.xy);
  p.x -= 2. * p.x * step (0., p.x);
  return p;
}


// ↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓
float rayTexSpeed = 0.2;

vec2 getDist(vec3 p) {


       float rayLen = 0.8;
    float ballScale = 1.;
    float sphereSize = 0.5;
    float sphereShift = sin(iTime * 5.);
     float boxRotation = .5 + .5 * sin(iTime) - 1.;
    float boxGlitch = 0.1;
    float rayAmp = 0.3;
    float rayFreq = 2.5;

  vec3 pplan = p;
  pplan.x += iTime * 40.;
  pplan.z -=  pow((sin(pplan.x) + sin(pplan.y)) / 2., 50.) * 10.;
    vec2 plane = vec2(pplan.z + 50., BG);
    plane.x *= 0.1;

  p.xz *= Rot(iTime / 2.);
  p = IcosSym (p);

  vec3 psph = p / ballScale;
  psph.z += 1.;
  psph.x += .8 * (.5 + .5 * sphereShift) * sin(PI / 3.);
  psph.y -= .8 * (.5 + .5 * sphereShift) * sin(PI / 6.);
  vec2 sph = vec2((length(psph) - sphereSize) * ballScale, BLUE);

  vec3 pbox = p * 2. / ballScale;
  pbox.yx *= Rot(PI / 4.);
  pbox.xz *= Rot(PI / 4.);
  pbox.x += boxRotation;
  vec2 box = vec2(sdBox(pbox, vec3(2)) * ballScale / 2., BLACK);
  box.x += boxGlitch * rnd(pbox.x + pbox.y + iTime);


vec3 pray = p / rayLen;
      pray.x += .8 * (.5 + .5 * sphereShift) * sin(PI / 3.);
      pray.y -= .8 * (.5 + .5 * sphereShift) * sin(PI / 6.);
    pray.xy *= Rot(pray.z * rayFreq);
    pray.x += rayAmp;
    //vec2 ray = vec2(length(pray.xy) - .015 * (.2 + .5 * sin(pray.z + iTime * 10.)), BLUE);
	vec2 ray = vec2(sdBox(pray, vec2(0.1 * (.5 + .5 * rayLen) + 0.02 * pray.z, 10.).xxy) * rayLen / 2., RAY);

    vec2 res = sph.x < box.x ? sph : box;
    res = res.x < ray.x ? res : ray;
    res = res.x < plane.x ? res : plane;
    return res;
}
// ↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑





vec3 rayMarch(vec3 ro, vec3 rd) {
	float d = 0.;
    float info = 0.;
    //float glow = 0.;
    float minAngleToObstacle = 1e10;
    for (int i = 0; i < MAX_STEPS; i++) {
    	vec2 distToClosest = getDist(ro + rd * d);
        minAngleToObstacle = min(minAngleToObstacle, atan(distToClosest.x, d));
        d += distToClosest.x;
        info = distToClosest.y;
        if(abs(distToClosest.x) < EPSILON || d > MAX_DIST) {
        	break;
        }
    }
    return vec3(d, info, minAngleToObstacle);
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(EPSILON, 0.);
    vec3 n = getDist(p).x - vec3(getDist(p - e.xyy).x,
                               getDist(p - e.yxy).x,
                               getDist(p - e.yyx).x);
	return normalize(n);
}



vec3 getRayDirection (vec3 ro, vec2 uv, vec3 lookAt) {
    vec3 rd;
    rd = normalize(vec3(uv - vec2(0, 0.), 1.));
    vec3 lookTo = lookAt - ro;
    float horizAngle = acos(dot(lookTo.xz, rd.xz) / length(lookTo.xz) * length(rd.xz));
    rd.xz *= Rot(horizAngle);
    return rd;
}

vec3 getRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // ray origin
