        //color_bw *= .5 + .5 * shadow;


        // tex *= color_bw;
        // color = tex;
    }
    color += 0.6 + vec3( color_bw );
    // coloring
    if (info == RAY) {
        color *= vec3(fract(length(p * 10.) - iTime * 10. * rayTexSpeed));
    }
    else if (info == BLUE) {
        color *= vec3(0.810,0.256,0.397);
    }
    else if (info == BLACK) {
        color *= vec3(0.130,0.130,0.130);
    }
    else if (info == BG) {
        color *= vec3(0.230,0.230,0.230);
    }
    color = mix(color, colorBg, smoothstep(150., 158., d));



    fragColor = vec4(color,1.0);
}

const float PI = 3.141592653;

float sector(vec2 c, vec2 p, float r, float sa, float alpha){
    float bl = 0.1;
    float l  = abs(distance(p, c ));
    float t  = smoothstep(r-bl, r + bl, l); 
    vec2  uv = p - c;
    //float a = atan(uv.y, uv.x);
    //if (a < 0.0) a += 2.0*PI;
    float a = PI - atan(uv.y, -uv.x);
    t  = a >= sa         ? t : 1.0;
    t  = a <= sa + alpha ? t : 1.0;
    return t;
}



