#define VAR_ZERO min (iFrame, 0)

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = (id);  qHit = q; }

vec2 SphGrid (vec3 p)
{
  vec3 q;
  vec2 a, sc, nSeg;
  float dMin, d1, d2, r;
  nSeg = vec2 (12., 15.);
  sc = sin (0.07 * 2. * pi / nSeg + vec2 (0., 0.5 * pi));
  q = p;
  r = length (q.yz);
  a = 2. * pi * (floor (nSeg * atan (q.zx, - vec2 (q.y, r)) / (2. * pi)) + 0.5) / nSeg;
  q.yz = Rot2D (q.yz, a.x);
  d1 = dot (vec2 (q.y, abs (q.z)), sc);
  q.yz = Rot2D (vec2 (r, q.x), a.y);
  d2 = dot (vec2 (q.y, abs (q.z)), sc);
  return vec2 (d1, d2);
}

float ObjDf (vec3 p)
{
  vec3 q, pp, qq;
  vec2 d2;
  float dMin, d, wThk, zOff, s, dw, r;
  dMin = dstFar;
  wThk = 0.07;
  zOff = -2.;
  pp = p;
  pp.x = abs (pp.x) - bSize.x;
  q = pp;
  d2 = SphGrid (q);
  d2.x = max (d2.x, 0.3 * winRad - length (q.yz)); 
  d = SmoothMin (d2.x, d2.y, 0.02);
  d = max (SmoothMax (abs (PrSphDf (q, winRad)) - wThk, d, 0.02), -0.01 - q.x);
  DMINQ (idWin);
  d = max (abs (PrSphDf (q, winRad + 0.5 * wThk)) - 0.25 * wThk, -0.01 - pp.x);
  DMINQ (idGlas);
  q = pp;
  q.x -= -0.05;
  d = PrCylAnDf (q.yzx, winRad - wThk + 0.2, 0.2, 0.05);
  q.yz = Rot2D (q.yz, 2. * pi * floor (12. * atan (q.z, - q.y) / (2. * pi) + 0.5) / 12.);
  q.xy -= vec2 (-0.1, - winRad - 0.1);
  d = max (d, - PrCylDf (q.yzx, 0.13, 0.1));
  DMINQ (idWEnd);
  d = max (abs (q.z) - 0.5 * min (abs (q.z) - sqrt3 * abs (q.y), 0.) - 0.1, abs (q.x - 0.05) - 0.05);
  DMINQ (idNut);
  q = p;
  dw = winRad - length (q.yz);
  q.z -= zOff;
  s = length (q.zx - vec2 (clamp (q.z, - bSize.z, bSize.z), 0.)) - bSize.x - 0.1;
  d = abs (max (s, abs (q.y) - bSize.y)) - 0.02;
  qq.y = q.y + bSize.y;
  qq.z = abs (q.z) - bSize.z - bSize.x + tunRad + 0.05;
  r = length (vec2 (q.x, qq.z));
  d = max (dw, max (d, min (tunRad + 0.05 - r, 0.2 - abs (qq.y + 0.1))));
  DMINQ (idWal);
  q.y = qq.y + 0.9;
  q.z = qq.z;
  d = min (max (abs (r - tunRad) - 0.05, abs (q.y) - 1.), q.y + 0.9);
  DMINQ (idTun);
  d = abs (s + 0.08) - 0.04;
  q = p;
  q.y = mod (q.y + 1., 2.) - 1.;
  q.z -= zOff;
  if (d < bSize.z - abs (q.z)) {
    q.z = mod (q.z + 1., 2.) - 1.;
  } else {
    q.z = abs (q.z) - bSize.z;
    q.zx = Rot2D (q.zx, pi / 12.);
    q.z = 0.85 * dot (q.zx, sin (2. * pi * (floor (12. * atan (q.x, - q.z) / (2. * pi)) +
       0.5) / 12. + vec2 (0., 0.5 * pi)));
  }
  d = max (dw + 0.34, SmoothMax (d, 0.05 - length (max (abs (q.yz) - 0.85, 0.)), 0.03));
  DMINQ (idWalB);
  q = p;
  q.z -= zOff;
  q.xz = abs (q.xz) - vec2 (bSize.x - 0.25, bSize.z + 0.8);
  d = PrCylDf (q.xzy, 0.1 + 0.03 * smoothstep (0.87, 0.9, abs (mod (q.y + 1., 2.) - 1.)), bSize.y);
  DMINQ (idPipe);
  q.xy -= vec2 (-0.15, -2.);
  d = PrCylDf (q.yzx, 0.3, 0.05);
  DMINQ (idPipe);
  q = p;
  q.z = abs (q.z - zOff) - bSize.z - bSize.x + 0.4;
  d = max (abs (q.y) - bSize.y - 2., PrCylDf (vec3 (q.x, mod (q.y + 0.5, 1.) - 0.5, q.z).yzx,
     0.09, 0.7));
  d = min (d, PrCylDf (vec3 (abs (q.x) - 0.7, q.yz).xzy, 0.11, bSize.y + 2.));
  DMINQ (idLad);
  q = p;
  q.yz -= vec2 (bSize.y - 1.6, 3.);
  d = PrCylDf (q.xzy, 0.15, 1.6);
  DMINQ (idPer);
  q.y -= -2.2;
  d = min (PrCapsDf (vec3 (q.x, abs (q.y) - 0.6, q.z).yzx, 0.05, 1.2),
     PrCapsDf (vec3 (abs (q.x) - 1.2, q.yz).xzy, 0.05, 0.6));
  DMINQ (idPer);
  d = PrBoxDf (q, vec3 (1.2, 0.6, 0.03));
  DMINQ ((q.z < 0.) ? idScrn : idPer);
  q = p;
  q.yz -= vec2 (-1.2, 1.5);
  d = PrCylDf (q, 0.1, 0.2);
  DMINQ (idWhl);
  q.xy = Rot2D (q.xy, whlAng);
  d = min (PrCylAnDf (q, 1.1, 0.1, 0.1), PrCylDf (q + vec3 (0., 0., -0.3), 0.2, 0.5));
  DMINQ (idWhl);
  q.xy = Rot2D (q.xy, 2. * pi * floor (12. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 12.);
  q.x += 0.8;
  d = PrCapsDf (q.yzx, 0.05, 0.8);
  DMINQ (idWhlS);
  q = p;
  q.yz -= vec2 (- bSize.y + 2.3, 2.55);
  d = PrRoundBoxDf (q, vec3 (0.3, 2.3, 0.3), 0.05);
  q.y -= 2.2;
  d = min (d, PrRoundBoxDf (q, vec3 (1.2, 0.1, 0.3), 0.05));
  DMINQ (idWhlB);
  q.x = abs (q.x) - 1.1;
  q.y -= 0.2;
  d = PrCylDf (q.xzy, 0.12, 0.1);
  DMINQ (idBras);
  q = p;
  q.yz -= vec2 (- bSize.y + 1.4, 2.55);
  d = PrCylDf (q.yzx, 0.1, 0.65);
  q.x = abs (q.x) - 0.5;
  d = min (d, PrCylDf (q.yzx, 0.7, 0.1));
  DMINQ (idWhlD);
  q = p;
  q.x = abs (q.x) - 2.;
  q.y -= - bSize.y + 0.5;
  q.z = (q.z > 0.) ? q.z - 6. : abs (q.z + 8.) - 2.;
  d = PrRoundBoxDf (q, vec3 (0.5, 0.5, 1.), 0.05);
  DMINQ (idBox);
  q.xy -= vec2 (bSize.x - 2.1, bSize.y - 2.);
  q.z = abs (q.z) - 0.15;
  d = PrCylDf (q.xzy, 0.05, 4.);
  DMINQ (idPipe);
  q.y = abs (q.y - 3.5) - 0.5;
  d = PrRoundBoxDf (q, vec3 (0.1, 0.25, 0.5), 0.05);
  DMINQ (idBox);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (2., - bSize.y + 0.7, 0.1);
  q.x = abs (q.x);
  d = max (- min (min (max (q.x - 0.07, abs (q.z) - 0.6), 0.), 0.18 - q.x),
     length (vec2 (q.y - min (q.y, 0.5), q.z)) - 0.7);
  d = min (min (d, PrCylDf (q.yzx, 0.1, 0.25)), PrCapsDf (q.xzy, 0.06, 2.));
  DMINQ (idLev);
  q = p;
  q.yz -= vec2 (-0.35, 2.65);
  r = length (q.xz);
  d = max (abs (r - 0.55) - 0.05, abs (q.y) - 0.1);
  DMINQ (idBras);
  d = max (r - 0.5, abs (q.y + 0.02) - 0.08);
  DMINQ (idComp);
  q = p;
  q.xz = abs (q.xz);
  q -= vec3 (bSize.x - 0.05, -1., 4.);
  d = PrCylDf (q.yzx, 0.35, 0.05);
  DMINQ (idDial);
  q.y -= -2.;
  d = PrCylDf (q.xzy, 0.05, 2.);
  DMINQ (idPipe);
  q = p;
  q = abs (vec3 (q.x, q.y + 4., q.z - zOff))- vec3 (bSize.x - 0.25, 0.5,  bSize.z - 1.7);
  q.z = abs (q.z) - 1.2;
  d = PrCapsDf (q, 0.4, 0.5);
  DMINQ (idFlot);
  q = p;
  q.xz = abs (vec2 (q.x, q.z - zOff));
  q -= vec3 (bSize.x, bSize.y - 1., 4.);
  q.z = abs (q.z) - 2.;
  d = PrCapsDf (q, 0.2, 0.5);
  DMINQ (idLit);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjAO (vec3 ro, vec3 rd)
{
  float ao, d;
  ao = 0.;
  for (int j = VAR_ZERO; j < 4; j ++) {
    d = 0.01 + float (j) / 20.;
    ao += max (0., d - 3. * ObjDf (ro + d * rd));
  }
  return 0.6 + 0.4 * clamp (1. - 2. * ao, 0., 1.);
}

