        w.xz = max (abs (ro.xz) - vec2 (1.6, 2.6), 0.);
        if (length (w.xz) < 0.5) col4 = vec4 (0.5, 0.2, 0., 0.1) *
           (0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.05, mod (8. * ro.z, 1.)));
      } else if (ro.y > bSize.y - 0.1) {
        col4 *= 0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.05, mod (4. * ro.z, 1.));
        col4 = mix (vec4 (0.8, 0.3, 0.3, 0.1), col4, step (0.2, length (vec2 (abs (ro.x) - 2.,
           mod (ro.z + 1., 2.) - 1.))));
        col4 = mix (vec4 (1., 1., 0.8, -1.), col4, smoothstep (0.03, 0.05, abs (abs (ro.x) - 3.)));
        col4 = mix (vec4 (0., 0.5, 0., -1.), col4, step (0.07, abs (length (vec2 (abs (ro.z + 2.) - bSize.z -
           bSize.x + tunRad + 0.05, ro.x)) - tunRad)));
      } else col4 = mix (vec4 (0.3, 1., 0.3, 0.), col4, smoothstep (0.05, 0.1, 
         max (abs (abs (ro.x) - 2.3), abs (ro.y) - 0.7)));
    } else if (idObj == idWalB) {
      col4 = vec4 (0.6, 0.7, 0.6, 0.1) * (0.8 + 0.2 * smoothstep (0.05, 0.1,
         length (abs (qHit.yz) - 1.)));
    } else if (idObj == idTun) {
      col4 = mix (vec4 (0.3, 0.7, 0.3, -1.), vec4 (0., 0.4, 0., 0.), 
         step (0., max (length (qHit.xz) - tunRad, 0.5 - qHit.y)));
    } else if (idObj == idLad) {
      col4 = vec4 (0.5, 0.5, 0.55, 0.1);
    }
  } else if (idObj <= idPipe) {
    if (idObj == idWin) {
      col4 = vec4 (0.7, 0.8, 0.7, 0.2) * (0.5 + 0.5 * step (0.02, abs (length (qHit) - winRad)));
    } else if (idObj == idWEnd) {
      col4 = vec4 (0.5, 0.55, 0.1, 0.2) * (0.5 + 0.5 * step (abs (ro.x), bSize.x - 0.1));
    } else if (idObj == idNut) {
      col4 = vec4 (0.4, 0.4, 0.5, 0.2);
    } else if (idObj == idPipe) {
      col4 = vec4 (0.3, 0.3, 0.3, 0.05);
    }
  } else if (idObj <= idWhlS) {
    if (idObj == idPer) {
      col4 = vec4 (0.4, 0.4, 0.42, 0.1);
    } else if (idObj == idWhl) {
      col4 = vec4 (0.7, 0.5, 0.2, 0.1) * (0.5 + 0.5 * step (0., min (abs (length (qHit.xy) - 1.1) - 0.02,
         length (qHit.xy) - 0.11)));
      col4 = mix (vec4 (0.4, 0.4, 0.8, 0.3), col4, step (0.05, length (vec2 (qHit.x, qHit.y - 1.1))));
    } else if (idObj == idWhlB) {
      col4 = mix (vec4 (0.8, 0.8, 0.8, -1.), vec4 (0.7, 0.5, 0.2, 0.05),
         smoothstep (0.01, 0.02, abs (abs (qHit.z) - 0.15)));
    } else if (idObj == idWhlS) {
      col4 = vec4 (0.7, 0.5, 0.2, 0.1) * (0.5 + 0.5 * step (0.05, abs (qHit.x + 0.7)));
    }
  } else if (idObj <= idDial) {
    if (idObj == idWhlD) {
      col4 = vec4 (0.7, 0.5, 0.2, 0.1) * (0.5 + 0.5 * step (0., min (abs (qHit.x) - 0.04,
         length (qHit.yz) - 0.105)));
    } else if (idObj == idComp) {
      if (qHit.y > 0.) {
        col4 = vec4 (1., 1., 1., 0.1);
        qHit.xz = Rot2D (qHit.xz, -0.5 * whlAng);
        col4 = mix (vec4 (0.8, 0.2, 0.2, 0.1), col4, step (0.05, length (vec2 (qHit.x, qHit.z - 0.4))));
        s = min (abs (qHit.x), abs (qHit.z));
        qHit.xz = Rot2D (qHit.xz, 2. * pi * floor (36. * atan (qHit.z, - qHit.x) /
           (2. * pi) + 0.5) / 36.);
        if (abs (qHit.x + 0.45) < 0.02) s = min (s, abs (qHit.z));
        col4 *= (0.1 + 0.9 * smoothstep (0.003, 0.008, s));
      } else col4 = vec4 (0.7, 0.7, 0., 0.2);
    } else if (idObj == idLev) {
      col4 = (length (qHit.xz) > 0.07) ? vec4 (0.7, 0.5, 0.2, 0.05) : vec4 (0.3, 0.4, 0.1, 0.05);
      col4 *= 0.5 + 0.5 * step (0., min (1.7 - qHit.y, length (qHit.yz) - 0.11));
    } else if (idObj == idDial) {
      if (length (qHit.yz) < 0.27) {
        if (ro.z < 0.) {
          s = 1.;
          a = atan (qHit.z, - qHit.y) / (2. * pi);
          if (abs (a) > 0.12) {
            s = abs (qHit.z);
            qHit.yz = Rot2D (qHit.yz, 2. * pi * floor (16. * a + 0.5) / 16.);
            if (abs (qHit.y + 0.25) < 0.02) s = min (s, abs (qHit.z));
          }
        } else {
          s = abs (dot (sign (Rot2D (qHit.yz, - pi * tCur)), vec2 (0.5)));
        }
        col4 = vec4 (1., 1., 1., 0.1) * (0.1 + 0.9 * smoothstep (0.01, 0.02, s));
      } else col4 = vec4 (0.7, 0.7, 0., 0.2);
    }
  } else if (idObj <= idLit) {
    if (idObj == idBras) {
      col4 = vec4 (0.7, 0.7, 0., 0.2);
      qHit.xz = abs (qHit.xz);
      if (abs (length (qHit.xz) - 0.55) < 0.04) col4 *= 0.5 +
         0.5 * smoothstep (0.005, 0.01, min (qHit.x, qHit.z));
    } else if (idObj == idBox) {
      if (abs (ro.x) < 3.) col4 = vec4 (0.3, 0.5, 0.4, 0.) * (0.5 + 
         0.5 * smoothstep (0.02, 0.03, abs (abs (qHit.x) - 0.1) - 0.01));
      else col4 = vec4 (0.5, 0.7, 0.6, 0.1) * (0.5 + 0.5 * smoothstep (0.02, 0.03, abs (qHit.y)));
    } else if (idObj == idFlot) {
      col4 = vec4 (0.7, 0.3, 0.1, 0.) * (0.5 + 0.5 * smoothstep (0.02, 0.03,
         abs (abs (qHit.z) - 0.3) - 0.05));
    } else if (idObj == idLit) {
      col4 = vec4 (vec3 (1., 1., 0.8) * (0.93 + 0.07 * cos (32. * pi * qHit.y)), -1.);
    }
  }
  return col4;
}

float WaveHt (vec2 p)
{
  mat2 qRot;
  vec4 t4;
  vec2 q, t, tw;
  float wAmp, h;
  qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  q = 0.05 * p + vec2 (0., 0.05 * tCur);
  wAmp = 1.;
  h = wvBase;
  tw = 0.1 * tCur * vec2 (1., -1.);
  for (int j = 0; j < 5; j ++) {
    q *= 2. * qRot;
    t4 = q.xyxy + tw.xxyy;
    t = vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw));
    t4 += 2. * t.xxyy - 1.;
    t4 = abs (sin (t4));
    t4 = (1. - t4) * (t4 + sqrt (1. - t4 * t4));
    t = 1. - sqrt (t4.xz * t4.yw);
    t *= t;
    h += wAmp * dot (t, t);
    wAmp *= 0.5;
  }
  return 0.4 * h * (1. - smoothstep (0.5, 1.2, length (p - vuPos.xz) / dstFar));
}

float WaveRay (vec3 ro, vec3 rd, float u)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  dHit = dstFar;
  ro.y *= u;
  rd.y *= u;
  s = 0.;
  sLo = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    p = ro + s * rd;
    h = p.y - u * WaveHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.5, 1.3 * h) + 0.01 * s;
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = VAR_ZERO; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (p.y > u * WaveHt (p.xz)) sLo = s;
      else sHi = s;
    }
    dHit = sHi;
  }
  return dHit;
}

vec3 WaveNf (vec3 p, float d)
{
  vec2 e;
  e = vec2 (max (0.1, 1e-4 * d * d), 0.);
  return normalize (vec3 (WaveHt (p.xz) - vec2 (WaveHt (p.xz + e.xy),
     WaveHt (p.xz + e.yx)), e.x)).xzy;
}

#if FISH

float FishDf (vec3 p)
{
  vec3 q;
  float dMin, dBodyF, dBodyB, dMouth, dFinT, dFinP, dFinD, dEye;
  p.x = abs (p.x);
  p.z -= 2.;
  p.yz = Rot2D (p.yz, 0.2 * fishAngI);
  q = p;
  q.z -= -0.6;
  dBodyF = PrEllipsDf (q, vec3 (0.7, 0.8, 2.4));
  q = p;
  q.z -= -1.2;
  q.yz = Rot2D (q.yz, fishAngI);
  q.z -= -1.6;
  dBodyB = PrEllipsDf (q, vec3 (0.35, 0.5, 2.5));
  q.z -= -2.2;
  q.yz = Rot2D (q.yz, 2. * fishAngI);
  q.xz -= vec2 (0.5, -0.5);
  q.xz = Rot2D (q.xz, 0.4);
  dFinT = PrEllipsDf (q, vec3 (0.8, 0.07, 0.4));
  q = p;
  q.yz -= vec2 (-0.3, 1.7);
  q.yz = Rot2D (q.yz, 0.1);
  q.y = abs (q.y) - 0.04;
  dMouth = PrEllipsDf (q, vec3 (0.25, 0.12, 0.6));
  q = p;
  q.yz -= vec2 (0.7, -1.);
  q.yz = Rot2D (q.yz, 0.6);
  dFinD = PrEllipsDf (q, vec3 (0.05, 1., 0.35));
  q = p;
  q.xy = Rot2D (q.xy, 0.8);
  q.xz -= vec2 (0.7, -0.1);
  q.xz = Rot2D (q.xz, 0.6);
  dFinP = PrEllipsDf (q, vec3 (0.9, 0.04, 0.3));
  q = p;
  q -= vec3 (0.4, -0.1, 1.1);
  dEye = PrSphDf (q, 0.1);
  idObjEx = 1;
  dMin = SmoothMin (dBodyF, dBodyB, 0.3);
  dMin = SmoothMin (SmoothMin (dMin, dFinT, 0.1), dMouth, 0.15);
  dMin = SmoothMin (SmoothMin (dMin, dFinD, 0.02), dFinP, 0.02);
  if (dEye < dMin) {
    idObjEx = 2;
    dMin = dEye;
  }
  return 0.9 * dMin;
}

float ExObjDf (vec3 p)
{
  p.x = abs (abs (p.x) - 18.) - 4.;
  return FishDf (p - fishPos);
}

float ExObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    d = ExObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ExObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ExObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

#endif

vec3 SkyGrndCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float sd, a, f, ff, fd;
  a = atan (rd.z, rd.x) + 0.001 * tCur * sign (rd.x);
  if (rd.y < 0.01 * Fbm1 (64. * a) - 0.005) {
    col = mix (vec3 (0.2, 0.35, 0.2), vec3 (0.3, 0.35, 0.35), 0.5 +
       0.5 * dot (normalize (rd.xz), - normalize (sunDir.xz))) *
       (1. - 0.3 * Fbm2 (128. * vec2 (a, rd.y)));
  } else {
    q = 0.01 * (ro.xz + vec2 (0., 8.) * tCur + ((200. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = 0.7 * (0.7 + 0.5 * ff) * (vec3 (1.) - vec3 (0.2, 0.2, 0.1) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    fd = smoothstep (0.01, 0.1, rd.y);
    col = mix (mix ((vec3 (0.7, 0.7, 0.75)), vec3 (0.4, 0.5, 0.8), 0.3 + 0.7 * fd), clCol,
       0.1 + 0.9 * f * fd);
    sd = pow (max (dot (rd, sunDir), 0.), 256.);
    col += (0.1 * sd + 0.3 * pow (sd, 4.)) * vec3 (1., 1., 0.3);
  }
  return col;
}

vec3 SeaFloorCol (vec3 rd)
{
  return mix (vec3 (0.05, 0.17, 0.12), vec3 (0., 0.15, 0.2), 
     smoothstep (0.4, 0.7, Fbm2 (2. * rd.xz / rd.y)));
}

vec3 UnwCol (vec3 rd)
{
  float t, gd, b;
  t = tCur * 2.;
  b = dot (vec2 (atan (rd.x, rd.z), 0.5 * pi - acos (rd.y)), vec2 (2., sin (rd.x)));
  gd = clamp (sin (5. * b + t), 0., 1.) * clamp (sin (3.5 * b - t), 0., 1.) +
     clamp (sin (21. * b - t), 0., 1.) * clamp (sin (17. * b + t), 0., 1.);
  return vec3 (0.1, 0.3, 0.4) * (0.2 + 0.4 * (rd.y + 1.)) * (1. + 0.3 * gd);
}

vec3 ExShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, vnw, rdd, rdo, colUw, ltDirF;
  float dstObj, dstWat, eta, atFac; 
  bool unWat, hitWat;
  eta = 1.33;
  atFac = 3.;
  rdo = rd;
  unWat = (ro.y < WaveHt (ro.xz));
  dstWat = WaveRay (ro, rd, (! unWat ? 1. : -1.));
#if FISH
  if (idObj == idGlas) {
    fishAngI = 0.1 * sin (pi * tCur);
    dstObj = ExObjRay (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      vn = ExObjNf (ro);
      if (idObjEx == 1) col4 = vec4 (vec3 (0.7, 0.7, 0.8) * (1. -
         0.2 * smoothstep (-0.5, -0.3, vn.y)), 0.1);
      else if (idObjEx == 2) col4 = vec4 (0.3, 1., 0.3, -1.);
      if (col4.a >= 0.) {
        ltDirF = normalize (vec3 (0., 1., 0.5));
        col = col4.rgb * (0.3 + 0.7 * max (dot (vn, ltDirF), 0.) +
           col4.a * pow (max (dot (reflect (rd, vn), ltDirF), 0.), 32.));
      } else col = col4.rgb * (0.7 - 0.3 * dot (vn, rd));
    }
  } else dstObj = dstFar;
#else
  dstObj = dstFar;
#endif
  hitWat = (dstWat < min (dstObj, dstFar));
  if (hitWat) {
    ro += dstWat * rd;
    vnw = WaveNf (ro, dstWat);
    if (! unWat) {
      rd = refract (rd, vnw, 1. / eta);
    } else {
      vnw *= -1.;
      rdd = refract (rd, vnw, eta);
      rd = (length (rdd) > 0.) ? rdd : reflect (rd, vnw);
    }
  }
  if (dstObj >= dstFar) col = (hitWat == unWat) ? SkyGrndCol (ro, rd) : SeaFloorCol (rd);
  if (! unWat) {
    if (hitWat) col = mix (col, 0.8 * SkyGrndCol (ro, reflect (rdo, vnw)),
       pow (1. - abs (dot (rdo, vnw)), 5.));
  } else {
    colUw = UnwCol (rd);
    if (dstObj < dstFar) col = mix (colUw, col, min (1., exp (- atFac * dstObj / dstFar)));
    else if (dstWat < dstFar) col = (rd.y > 0.) ? mix (colUw, col,
       exp (- atFac * dstWat / dstFar)) : colUw;
    else col = colUw;
  }

  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, ltDirL[5];
  vec2 vf;
  float dstObj, nDotL, dSum, sSum, ao, wd;
  bool isMet, exVu;
  bSize = vec3 (4.5, 5.1, 10.);
  winRad = 2.5;
  tunRad = 1.55;
  dstObj = ObjRay (ro, rd);
  exVu = (dstObj < dstFar && (idObj == idScrn || idObj == idGlas));
  if (dstObj < dstFar && ! exVu) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = ObjCol (ro);
    vf = vec2 (0.);
    if (idObj == idWal || idObj == idWalB || idObj == idWin || idObj == idWEnd || idObj == idTun ||
       idObj == idBox) {
      vf = vec2 (64., 0.5);
    } else if (idObj == idNut || idObj == idPipe) {
      vf = vec2 (128., 0.2);
    } else if (idObj == idWhl || idObj == idWhlS || idObj == idWhlD) {
      vf = vec2 (128., 0.1);
    } else if (idObj == idWhlB || idObj == idLev) {
      vf = vec2 (64., 0.2);
    }
    wd = smoothstep (-0.7, 0.7, wvBase);
    if (col4.a >= 0.) {
      if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
      ltDirL[0] = normalize (bSize * vec3 (1., 0.7, 1.));
      ltDirL[1] = normalize (bSize * vec3 (-1., 0.7, 1.));
      ltDirL[2] = normalize (bSize * vec3 (1., 0.7, -1.));
      ltDirL[3] = normalize (bSize * vec3 (-1., 0.7, -1.));
      ltDirL[4] = ltDir;
      dSum = 0.;
      sSum = 0.;
      isMet = (idObj == idWal || idObj == idWalB || idObj == idComp || idObj == idDial);
      for (int k = 0; k < 5; k ++) {
        nDotL = max (dot (vn, ltDirL[k]), 0.);
        if (isMet) nDotL *= nDotL;
        dSum += ((k < 5) ? 0.7 : 1.) * nDotL;
        sSum += ((k < 5) ? 0.7 : 1.) * pow (max (dot (normalize (ltDirL[k] - rd), vn), 0.), 64.);
      }
      ao = ObjAO (ro, vn);
      col = ao * col4.rgb * (0.2 + 0.4 * dSum) + step (0.95, ao) * col4.a * sSum;
      col *= mix (vec3 (1.), vec3 (0.6, 0.8, 1.), wd);
    } else col = col4.rgb * (0.7 - 0.3 * dot (vn, rd)) * (1. - 0.5 * wd);
  }
  if (exVu) {
    if (idObj == idGlas) {
      ro += dstObj * rd;
    } else {
      ro = vec3 (0., 3., 3.);
      rd = normalize (vec3 (qHit.xy, 2.));
    }
    col = ExShowScene (ro, rd);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv;
  float el, az, zmFac, asp, sr, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  az = 0.;
  el = -0.05 * pi;
  if (mPtr.z > 0.) {
    az += 2.5 * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    t = mod (0.03 * tCur, 2.);
    az += 0.5 * pi * SmoothBump (0.25, 0.75, 0.15, mod (t, 1.)) * sign (t - 1.);
    el += 0.1 * abs (az);
  }
  el = clamp (el, -0.4 * pi, 0.4 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -4.5);
  ro.z *= 1. + abs (cos (az));
  vuPos = ro;
  zmFac = 3. + 1.5 * abs (sin (az));
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (0.5, 0.5, -1.));
  sunDir = normalize (vec3 (1., 1., -1.));
  whlAng = 0.2 * pi * sin (0.05 * pi * tCur);
  wvBase = 2. + 7. * sin (0.03 * pi * tCur);
#if FISH
  fishPos = vec3 (0., wvBase - 8., mod (45. + 2. * tCur, 90.) - 45.);
#endif
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a +
       0.5) * pi)) / zmFac;
    uvv.x = 2. * tan (0.5 * atan (uvv.x / asp)) * asp;
    rd = vuMat * normalize (vec3 (uvv, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

