        rayPos += sdfRN*raydir;
        totalDist += sdfRN;
        if(sdfRN<EPSILON*2.0){
            return totalDist;
        	break;
        }

        if(sdfRN>MAXDIST){
            return float(MAXDIST);
        	break;
        }
    }
