float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = getmoonBinarySDF(ro + rd*t,0.7);
        if( h<EPSILON )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}

float hardshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = getmoonBinarySDF(ro + rd*t,1.0);
        if( h<EPSILON )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}
float getLightIntense(vec3 normal,vec3 lightdir,vec3 viewdir,float spec,float expo,float diff){
