    uv.x = (uv.x + 0.5 * aspectRatio ) * 2.0;
    uv.y = uv.y + 0.5;// / aspectRatio ;


    float k = (( ( iMouse.xy / iResolution.xy ) ) * 5.0).x; 

    vec3 col = vec3(0);

    //k = 0.0;

    float a = sqrt(uv.x);
    float b = ( 2.0 - uv.x ) * ( 2.0- uv.x );
    //float minValue = min( a , b );
    float h2 = max( 0.0 , min( 1.0 , (b-a)/k + 0.5));
    float m = h2 * ( 1.0 - h2 ) * k;
    float l = h2 * a + ( 1.0 - h2 ) * b - m *0.5;



    col =vec3(1) -  vec3( step( l , uv.y) )* vec3( 0.8 , 0.2 , 0.1 );





    fragColor = vec4(col,1.0);
}

// 'Concrete++'
//
// I wanted a procedural 3D concrete texture for future projects, written in a way which
// can be easily shared.
// See getConcreteMaterial() for the texture code.
//
// Thanks to @Shane, there's no need for any texture channels to be used.
//
// The corridor, etc is just a demo so I can see how it all looks in a 3D scene.
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane
// and a bunch of others for sharing their knowledge!

// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define MIN_DIST 0.0015

#define AA  // Comment out this line for a speed boost.

// Thanks Shane - https://www.shadertoy.com/view/lstGRB
float noise(vec3 p) {
	const vec3 s = vec3(7.0, 157.0, 113.0);
	vec3 ip = floor(p);
    vec4 h = vec4(0.0, s.yz, s.y + s.z) + dot(ip, s);
	p -= ip;

    h = mix(fract(sin(h) * 43758.5453), fract(sin(h + s.x) * 43758.5453), p.x);

    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z);
}

mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

vec2 opRep(vec2 p, vec2 c) {
    return mod(p + 0.5 * c, c) - 0.5 * c;
}

float opRep(inout float p, float size, float c1, float c2) {
    float w2 = size * 0.5;
    float pp = p - w2;
    float idx = clamp(floor(pp / size), c1, c2);
    p = pp - size * idx - w2;
    return fract(idx * 12532.56);
}

float opRep(inout vec2 p, vec2 size, vec2 c1, vec2 c2) {
    vec2 w2 = size * 0.5;
    vec2 pp = p - w2;
    vec2 idx = clamp(floor(pp / size), c1, c2);
    p = pp - size * idx - w2;
    return dot(fract(idx * 12532.56), vec2(421.0, 965.0));
}

vec4 min4(vec4 a, vec4 b) { return a.x < b.x ? a : b; }

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdCappedCylinder(vec3 p, float h, float r) {
  vec2 d = abs(vec2(length(p.xy), p.z)) - vec2(h, r);
  return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    return normalize(forward + right * uv.x + up * uv.y);
}

// Call to create a concrete-like 3D texture.
//
// p:   [in]  point in 3D space.
// mat: [out] material rgb
// id:  [in]  arbitary ID used as a rnd seed.
// dist:[in]  distance from ray origin (Used to fade out surface displacement, avoiding rendering artifacts).
// returns: surface noise value to apply to a SDF function.
float getConcreteMaterial(vec3 p, out vec3 mat, float id, float dist) {
    // Set rnd seed from id.
    vec3 tp = p + vec3(0.32, 0.40, 1.2) * mod(id, 10.0);

    // Mix a couple of shades of grey.
    float baseColor = smoothstep(0.0, 0.5, noise(tp));
    mat = mix(vec3(0.18, 0.17, 0.17), vec3(0.20, 0.19, 0.19), baseColor);

    // Surface roughness.
    dist = 1.0 - smoothstep(0.0, 1.0, dist / 14.0);
    float rough = noise(tp * 60.0) * 0.005 // Base
         		  + step(0.2, noise(tp * 26.666)) * 0.0033; // Pits/dents.
    mat += rough * 24.0;

    // Fade surface roughness(/deflection) out with distance to prevent screen noise.
    return rough * dist;
}

// A rectangular concrete 'wall panel', with a small hole in each corner.
//
// p: point in 3D space.
// r: dimensions (halved)
// id: arbitary ID used as a rnd seed.
// dist: distance from ray origin (Used to fade out surface displacement, avoiding rendering artifacts).
// returns (distance, material rgb)
vec4 sdConcretePanel(vec3 p, vec3 r, float id, float dist) {
    // Tilt each panel a small amount.
    mat2 tilt = rot((fract(sin(id) * 43758.5453) - 0.5) * 0.03);
    p.xz *= tilt;
    p.yz *= tilt;

    float d = sdBox(p, r - 0.04) - 0.04;

    // Mounting holes.
    vec3 pp = p;
    pp.xy = abs(p.xy) - r.xy * 0.8;
    float hole = sdCappedCylinder(pp + vec3(0.0, 0.0, r.z * 1.5), r.x * 0.03, r.z * 1.0);
    d = max(d, -hole);

    // Apply material.
    vec3 mat;
    d -= getConcreteMaterial(p, mat, id, dist);
    return vec4(d, mat);
}

// Concrete paving slab.
//
// p: point in 3D space.
// r: dimensions (halved)
// id: arbitary ID used as a rnd seed.
// dist: distance from ray origin (Used to fade out surface displacement, avoiding rendering artifacts).
// returns (distance, material rgb)
vec4 sdSlab(vec3 p, vec3 r, float id, float dist) {
    // Tilt each slab a small amount.
    mat2 tilt = rot((fract(sin(id) * 43758.5453) - 0.5) * 0.05);
    p.xz *= tilt;
    p.yz *= tilt;

    float d = sdBox(p, r - 0.04) - 0.04;

    // Apply material.
    vec3 mat;
    d -= getConcreteMaterial(p, mat, id, dist);
    return vec4(d, mat);
}

// Map the scene using SDF functions.
vec4 map(vec3 p, vec3 ro) {
    vec3 r = vec3(1.0, 2.0, 0.05);

    vec4 d = vec4(1e10);

    float dist = distance(p, ro);

    // Left wall.
    vec3 pp = p;
    pp.xz *= rot(3.141 / 2.0);
    pp.z -= 5.0;
    float id = opRep(pp.xy, r.xy * 2.0, vec2(-15.0, -2.0), vec2(10.0, 1.0));
    d = min4(d, sdConcretePanel(pp, r, id, dist));

    // Far wall.
    pp = p;
    id = opRep(pp.xy, r.xy * 2.0, vec2(-5.0, -2.0), vec2(5.0, 1.0));
    d = min4(d, sdConcretePanel(pp - vec3(0.0, 0.0, 33.0), r, id, dist));

    // Columns.
    pp = p - vec3(8.0, 0.0, 0.0);
    id = opRep(pp.z, 5.0, -4.0, 3.0);
    d = min4(d, sdSlab(pp, vec3(2.0, 8.0, 1.0), 1.0, dist));

    // Floor.
    pp = p;
    pp.yz *= rot(3.141 / 2.0);
    pp.z -= 5.0;
    r = vec3(3.0, 3.0, 0.1);
    id = opRep(pp.xy, r.xy * 2.0, vec2(-5.0, -5.0), vec2(6.0, 6.0));
    d = min4(d, sdSlab(pp, r, id, dist));

    // Ceiling.
    d = min4(d, sdSlab(p - vec3(1.0, 10.0, 0.0), vec3(10.0, 3.0, 50.0), 1.0, dist));

    return d;
}

vec3 calcNormal(vec3 p, vec3 ro) {
    vec2 e = vec2(1.0, -1.0) * 0.005773;
    return normalize(e.xyy * map(p + e.xyy, ro).x + 
					 e.yyx * map(p + e.yyx, ro).x + 
					 e.yxy * map(p + e.yxy, ro).x + 
					 e.xxx * map(p + e.xxx, ro).x);
}

float calcShadow(vec3 p, vec3 ro, vec3 lightPos) {
    vec3 rd = normalize(lightPos - p);

    float res = 1.0;
    float t = 0.05;
    for(float i = 0.0; i < 45.0; i++)
    {
        float h = map(p + rd * t, ro).x;
        if (h < MIN_DIST * t)
            return 0.0; // Hit an object - Full shadow.

        res = min(res, 48.0 * h / t);
        t += h;

        if (t > 20.0)
            break; // Marched far enough - Stop.
    }

    return res;
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 ro, vec3 n, float h) {
    return map(p + h * n, ro).x / h;
}

/**********************************************************************************/

vec3 vignette(vec3 col, vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    col *= 0.5 + 0.5 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.4);
    return col;
}

vec3 applyLighting(vec3 p, vec3 rd, vec3 ro, vec3 mat) {
    vec3 sunCol = vec3(2.0, 1.6, 1.4);
    vec3 sunPos = vec3(100.0, 50.0, -10.0);
    vec3 sunDir = normalize(sunPos - p);
    vec3 n = calcNormal(p, ro);
    float amb = dot(vec3(ao(p, ro, n, 0.2), ao(p, ro, n, 0.5), ao(p, ro, n, 2.0)), vec3(0.2, 0.3, 0.5));

    // Diffuse color.
    float diff = max(0.0, dot(sunDir, n));

    // Primary light (Sun).
    float sha = calcShadow(p, ro, sunPos) + 0.25;
    vec3 lig = diff * sunCol * sha;

    // Specular.
    lig += pow(max(0.0, dot(rd, reflect(normalize(sunPos - p), n))), 15.0);

    // Seconary light (Bounce light);
    lig += max(0.0, dot(-sunDir, n)) * sunCol * 0.1 * amb;

    return mat * lig;
}

vec4 march(vec3 ro, vec3 rd, out vec3 p) {
    float d = 0.01, prevd = 1.0;
    vec4 details;
    for (float steps = 0.0; steps < 64.0; steps++) {
        p = ro + rd * d;
        details = map(p, ro);

        if (abs(details.x) < MIN_DIST)
            break; // We've hit a surface - Stop.
        if (d > 35.0) return vec4(1e7, 0.0, 0.0, 0.0);

        d += details.x; // No hit, so keep marching.
    }

    return details;
}

vec3 getSceneColor(vec3 ro, vec3 rd) {
    // Raymarch.
    vec3 p;
  	vec4 details = march(ro, rd, p);

    // Materials and lighting.
    return applyLighting(p, rd, ro, details.yzw);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Camera.
    float ft = smoothstep(0.0, 1.0, fract(iTime / 9.5));
    float phase = mod(floor(iTime / 9.5), 3.0);
    float dim = 1.0 - pow(abs(cos(iTime * 3.141 / 9.5)), 100.0);

    vec3 ro;
    vec3 lookAt;

    if (phase == 0.0) {
        ro = vec3(mix(22.0, 9.0, ft), 0.0, 8.0);
        lookAt = vec3(6.0, 0.0, mix(5.0, 13.0, ft));
    } else if (phase == 1.0) {
        ro = vec3(0.0, 0.0, mix(-9.0, 4.5, ft));
        lookAt = vec3(0.0, mix(-10.0, 0.0, ft), mix(0.0, 8.0, ft));
    } else if (phase == 2.0) {
        ro = vec3(-1.0, 0.0, mix(-4.0, 0.0, ft));
        lookAt = vec3(-5.0, mix(-1.0, 1.0, ft), ro.z + mix(0.0, 5.0, ft));
    }

    vec3 col = vec3(0.0);
#ifdef AA
    for (float dx = 0.0; dx <= 1.0; dx++) {
        for (float dy = 0.0; dy <= 1.0; dy++) {
            vec2 coord = fragCoord + vec2(dx, dy) * 0.5;
#else
            vec2 coord = fragCoord;
#endif
            vec2 uv = (coord - 0.5 * iResolution.xy) / iResolution.y;

            vec3 rd = getRayDir(ro, lookAt, uv);
            col += getSceneColor(ro, rd);
#ifdef AA
        }
    }
    col /= 4.0;
#endif

    col *= dim;

    // Output to screen.
    col = vignette(pow(col, vec3(0.4545)), fragCoord);
    fragColor = vec4(col, 1.0);
}

