        float distToLight = length(light - p);
        // if (distToObstable < distToLight) {
        //     color_bw =  0.;
        // }

        // smooth shadows
        float shadow = smoothstep(0.0, .1, rayMarchLight.z / PI);
        color_bw *= .7 + .3 * shadow;


        // tex *= color_bw;
        // color = tex;
    }
    color += 0.6 + vec3( color_bw );
    // coloring
    if (info == IVORY) {
        color *= vec3(0.433,0.457,0.545);
