    obj O ;
    bool hit = false;
    float shad;
    float t=0.;
    for(float i=0.;i < ITER; i++) {
        p = ro + t*rd;
        O = SDF(p);
        if(O.d< 0.01) {
            hit = true;
            shad = i / ITER;
            break;
        }
        t+=O.d;
