    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}
//////////////////////////////////////////////////// NOISE CODE END ////////////////////////////////////////////////////

vec4 plane(float currHeight, float height, vec3 color, vec2 uv, float time , float planeScale, vec2 off)
