// raymarching and lighting from https://www.shadertoy.com/view/MltcDB

#define SKY_COLOR vec3(0.0, 0.18, 0.2)

#define BASE_COLOR vec3(0.4, 0.6, 0.6)
#define BOAT_COLOR vec3(1., 0.57, 0.0)
#define HEAD_COLOR vec3(.7, .6, .4)

#define SHARK_COLOR vec3(.5, .5, .5)

#define SHADOW_COLOR vec3(1.0)


#define BOAT_INITIAL_X -0.25
#define BOAT_INITIAL_Z -0.3


#define X_WAVE_SPACE 0.05
#define Z_WAVE_SPACE 0.05

#define WAVE_SMOOTH 0.01
#define BOAT_SMOOTH 0.03



#define STEPS 64.
#define PI 3.14159
#define EPS 0.0001
#define EPSN 0.001

mat2 rot(float a){
	return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float smoothmin(float a, float b, float k){
	float f = clamp(0.5 + 0.5 * ((a - b) / k), 0., 1.);
    return (1. - f) * a + f  * b - f * (1. - f) * k;
}


float distSphere(vec3 p, vec3 center, float radius) {
	return length(p-center) - radius;   
}


vec3 coordsFromGrid(vec3 grid) {
    return grid / 10.;
}

vec3 gridFromCoords(vec3 coords) {
    return coords * 10.;
}

float waveDephthAt(vec3 coords) {
    return 0.10 
        + 0.03 * cos(0.5 * coords.x + 1.2 * sin(0.5*coords.z + iTime) + 5.*iTime) 
        + 0.002 * cos(cos(2.*coords.x) + sin(2.*coords.z) + 4. * iTime)
        ;   
}


float boatYAt(vec3 coords) {
    return 0.01 + waveDephthAt(coords);   
}


float distWaves(vec3 pos) {
     vec3 grid = gridFromCoords(pos);
    vec3 nGrid = vec3(grid.x, 0., grid.z);
    vec3 coords = coordsFromGrid(vec3(nGrid.x, 0., nGrid.z));            

    float seaHeight = waveDephthAt(vec3(nGrid.x, 0., nGrid.z));
    float dist = distSphere(pos, coords, seaHeight);

	return dist;
}

vec3 transformedFlotterPosition(vec3 position) {
    position.xz = rot(0.35 * cos(iTime)) * position.xz;
    vec3 translation = vec3(BOAT_INITIAL_X + 0.1 * cos(0.5*iTime) + 0.05 * cos(0.95*iTime), 0., BOAT_INITIAL_Z + 0.1 * cos(0.8*iTime)); 
    vec3 transformedFlotterPosition = position + translation;
	return transformedFlotterPosition;
}

float distBoat(vec3 pos, out vec3 headPosition) {

    vec3 position = transformedFlotterPosition(vec3(0., 0., 0.));
    vec3 p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    float distBoat = distSphere(pos, p, 0.05);

    position = transformedFlotterPosition(vec3(0.05, 0., 0.));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);

    position = transformedFlotterPosition(vec3(0.1, 0., 0.));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);

    position = transformedFlotterPosition(vec3(0.15, 0., 0.));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);



    position = transformedFlotterPosition(vec3(0.0, 0., 0.05));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);

    position = transformedFlotterPosition(vec3(0.15, 0., 0.05));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);




    position = transformedFlotterPosition(vec3(0.0, 0., 0.1));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);

    position = transformedFlotterPosition(vec3(0.05, 0., 0.1));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);

    position = transformedFlotterPosition(vec3(0.1, 0., 0.1));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);

    position = transformedFlotterPosition(vec3(0.15, 0., 0.1));
    p = vec3(position.x, boatYAt(gridFromCoords(position)), position.z);
    distBoat = smoothmin(distBoat, distSphere(pos, p, 0.05), BOAT_SMOOTH);


    headPosition = transformedFlotterPosition(vec3(0.05, 0., 0.05));


	return distBoat;
}

float distHead(vec3 pos, vec3 headPosition) {
    vec3 p = vec3(headPosition.x, boatYAt(gridFromCoords(vec3(headPosition.x - 0.05, headPosition.y, headPosition.z))) + 0.05, headPosition.z);

    float headDist = distSphere(pos, p, 0.02);

