     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );

	return d;
}


float distScene(vec3 pos, out vec3 colorVariation, out float shineVariation){
    // pos.xz = rot(1.5 * sin(0.25 * iTime)) * pos.xz;
    pos.y += 0.35;

    colorVariation = vec3(1.);

    // waves

    float dist = distWaves(pos);
    colorVariation = BASE_COLOR;
    shineVariation = 1.;


	// boat
    vec3 headPosition;
	float boatDist = distBoat(pos, headPosition);
    if (boatDist < dist) {
        dist = boatDist;
   	 	colorVariation = BOAT_COLOR;
        shineVariation = .6;
    }


    // head 
    float headDist = distHead(pos, headPosition);
    if (headDist < dist) {
     	dist = headDist;
   	 	colorVariation = HEAD_COLOR;
		shineVariation = .0;
    }


    return dist;
}

vec3 getNormal(vec3 p){
    vec3 c;
    float s;
	return(normalize(vec3(distScene(p + vec3(EPSN, 0., 0.), c, s) - distScene(p - vec3(EPSN, 0., 0.), c, s),
               			distScene(p + vec3(0., EPSN, 0.), c, s) - distScene(p - vec3(0., EPSN, 0.), c, s),
               			distScene(p + vec3(0., 0., EPSN), c, s) - distScene(p - vec3(0., 0., EPSN), c, s))));
}


vec3 render(vec2 uv){
	vec3 col = BASE_COLOR;

    //camera
    vec3 eye = vec3(0., 1., 5.);
    vec3 ray = normalize(vec3(uv, 1.) - eye);
  	ray.yz = rot(0.08) * ray.yz;
   	eye.yz = rot(0.12) * eye.yz;


    // moon
    vec2 moonPosition = vec2(0.19, 0.20);
    if (length(moonPosition - uv) < 0.12) {
   	 	return vec3(0.9);
    }


   	//raymarch to the scene
    vec3 pos = eye;
    vec3 colorVariation;
    float shineVariation;
    float s, prevDist, totalDist = 0.;
    float maxDist = 5.8;

    float dist = distScene(pos, colorVariation, shineVariation);
    for(float s = 0.; s < STEPS; s++){
        prevDist = dist;
    	dist = distScene(pos, colorVariation, shineVariation);
        if(abs(dist) < EPS || totalDist > maxDist){
            break;
        }
        pos += ray * dist;
        totalDist += dist;
    }

    if(totalDist > maxDist) {
        return SKY_COLOR;
    }


    vec3 normal = getNormal(pos);
    vec3 light = normalize(vec3(1., 1., -3));
    light.yz = rot(0.3) * light.yz;
    col = colorVariation * (1. + dot(normal, light));


    // raymarch back toward the light to compute the shadows
    light = normalize(vec3(1., 1.3, -3)); // tweak light to avoid the waves casting shadows on themselves

    float shadow = 1.;
    totalDist = 0.01;
    dist = 0.0;
	float sv;
    vec3 cv;
    for(float s = 0.; s < STEPS; s++){
        dist = distScene(pos + light * totalDist, cv, sv);
        totalDist += dist;
        shadow = min( shadow, 10.0*dist/totalDist );
        if(shadow < 0.001 || totalDist > maxDist){
            break;
        }
    }
    shadow = clamp(shadow, 0., 1.);
    col = col * (0.5 + 0.5*shadow);


    //white highlight
    if (shadow > 0.9) { // we don't want highlights in the shade, notably from the boat
		light = normalize(vec3(1., 0.5, -3)); // tweak light to get higher shine
        float shine = 10.;
        vec3 refl = reflect(normalize(pos - light), normal);
        float spec = pow(clamp(dot(normalize(eye - pos), refl), 0., 1.), shine);
        spec = smoothstep(0.25, 0.45, spec - 0.4);
        col = mix(col, vec3(1.), (shineVariation -  0.8 * colorVariation) * spec);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.x;
    vec3 col = render(uv);

    fragColor = vec4(col,1.0);
}

// All the distance functions from:https://iquilezles.org/articles/distfunctions2d
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) 1.0-smoothstep(b,antialiasing(1.0),d)

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }


float auxeticDistance(vec2 p) {
	float d = length(p+vec2(0.15,0.0))-0.1;
    float d2 = length(p+vec2(-0.15,0.0))-0.1;
    d = min(d,d2);
    d2 = sdBox(p,vec2(0.1,0.05));
    d = opSmoothUnion(d,d2,0.05);
    return abs(d)-0.02;
}

vec3 auxeticPattern(vec2 p, vec3 col, vec3 rhcol){
    p.y-=iTime*0.1;
    float smoothval = -0.015;
    vec2 pref = p;
    float scale = 3.0;
    p*=scale;

    p.x = mod(p.x,0.75)-0.375;
    p.y = mod(p.y,0.7)-0.35;

    float d = auxeticDistance(p);
    col = mix(col, rhcol,S(d,smoothval));

    p = pref;
	p*=scale;

    p.y += 0.35;
    p.x = mod(p.x,0.75)-0.375;
    p.y = mod(p.y,0.7)-0.35;
    d = auxeticDistance(p*Rot(radians(90.0)));
    col = mix(col, rhcol,S(d,smoothval));

	p = pref;
	p*=scale;

    p.x += 0.37;
    p.y += 0.35;
    p.x = mod(p.x,0.75)-0.375;
    p.y = mod(p.y,0.7)-0.35;
	d = auxeticDistance(p);
    col = mix(col, rhcol,S(d,smoothval));

	p = pref;
	p*=scale;

	p.x += 0.37;
    p.y += 0.7;
    p.x = mod(p.x,0.75)-0.375;
    p.y = mod(p.y,0.7)-0.35;
	d = auxeticDistance(p*Rot(radians(90.0)));
    col = mix(col, rhcol,S(d,smoothval));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(126.0/255.0,0.0/255.0,67.0/255.0);
	vec3 baseCol = vec3(1.0);

    col = auxeticPattern(uv,col,baseCol);

    fragColor = vec4(col,1.0);
}

