// -SETTINGS-

// The initial density of the life.
# define INIT_DENSITY 0.1
// Usually two parents is not enought to reproduce according to the vanilla rules.
// Set the below define to "0." if you want vanilla rules.
// If the below define is higher, the walkers will be able to colonize.
# define TWO_PARENT_REPRODUCE_CHANCE 0.0002

// Noise function found at https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl
uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}
uint hash( uvec2 v ) { return hash( v.x ^ hash(v.y)); }
uint hash( uvec3 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z)); }
//

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 pC = texelFetch(iChannel0, ivec2(fragCoord), 0); // Previous color.

    if(iTime < 1.){ // Initialize with random noise.
        float prob = float(hash(uvec2(fragCoord))) / 4294967295.;
        bool alive = prob < INIT_DENSITY;
    	fragColor = vec4( alive, alive, 0, 1.0);
        return;
    }

    ivec2 myC = ivec2(fragCoord);

    // Game of life logic.
    // Red logic.
    bool redAlive;
    int numNeighborsRed =
		int(texelFetch(iChannel0, myC + ivec2(-1, 1), 0).x > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 0, 1), 0).x > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 1, 1), 0).x > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2(-1,0), 0).x > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 1,0), 0).x > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2(-1,-1), 0).x > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 0,-1), 0).x > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 1,-1), 0).x > 0.5)
    ;
    if(pC.x > 0.5){ // Is alive.
        if(numNeighborsRed < 2){
        	redAlive = false;
        } else if (numNeighborsRed < 4){
        	redAlive = true;
        } else {
        	redAlive = false;
        }
    } else { // Is dead.
    	if(numNeighborsRed  == 3){
        	redAlive = true;
        } else {
        	redAlive = false;
        }
        // Special rule.
        float prob = float(hash(uvec2(fragCoord) + uvec2(iTime*60.))) / 4294967295.;
        if(numNeighborsRed == 2 && prob < TWO_PARENT_REPRODUCE_CHANCE){
        	redAlive = true;
        }
    }
    // Green logic.
    bool greenAlive;
    int numNeighborsGreen =
		int(texelFetch(iChannel0, myC + ivec2(-1, 1), 0).y > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 0, 1), 0).y > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 1, 1), 0).y > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2(-1,0), 0).y > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 1,0), 0).y > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2(-1,-1), 0).y > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 0,-1), 0).y > 0.5) +
		int(texelFetch(iChannel0, myC + ivec2( 1,-1), 0).y > 0.5)
    ;
    if(pC.y > 0.5){ // Is alive.
        if(numNeighborsGreen < 2){
        	greenAlive = false;
        } else if (numNeighborsGreen < 4){
        	greenAlive = true;
        } else {
        	greenAlive = false;
        }
    } else { // Is dead.
    	if(numNeighborsGreen  == 3){
        	greenAlive = true;
        } else {
        	greenAlive = false;
        }
    }

    fragColor = vec4(float(redAlive), float(greenAlive), 0.0, 1.0);
}

