    }

    if (keyIsDown(KEY_W) == false) { //Adds in new wave      
    	t = mix(t, vec2(sin(iTime*f), cos(iTime*f)), .5*exp(-.2*length(fragCoord-iResolution.xy/2.)));
    }

    vec2 border = texture(iChannel2, fragCoord/iResolution.xy).xy; //adds in the custom border shape

    if(fragCoord.x<1. || fragCoord.y<1. || fragCoord.x>iResolution.x-1. || fragCoord.y>iResolution.y-1. || (border.x == 1.0 && keyIsDown(KEY_E)==false)){
        t*=0.;
    }

    if ( keyIsDown(KEY_R)) {
        t *= 0.;
    }


    fragColor = vec4(t.x,t.y,0,1);
}

