  //p.x = abs(p.x);
  //p.xy *= Rot(PI / 3.);
  //p.xy *= Rot(PI / 3.);
  //p.x = abs(p.x);
  vec3 p2 = p;
  p2.xy *= Rot(1.9106329);
  r2 = sdRay(p2);
  //p.xz *= Rot(1.9106329);
  vec3 p3 = p;
  p3.xz *= Rot(2. * PI / 3.);
  p3.xy *= Rot(1.9106329);
  r3 = sdRay(p3);
  vec3 p4 = p;
  p4.xz *= Rot(- 2. * PI / 3.);
