vec2 getDist(vec3 p) {
  p.y += 0.2;
  p.xz *= Rot(iTime);
  float sphere = (length(p) - 1.5) * .5;
  float fractal = sdStar(p);
  float scale = 0.7;
  vec3 shift = vec3(0, -1.5, 0);

  for (int i = 0; i < 1; i ++) {
    //p.xy *= Rot(PI / 3.);
    //p.x = abs(p.x);
    //p.xy *= Rot(PI / 3.);
    //p.x = abs(p.x);
    //p /= scale;
    //p.xy *= Rot(PI / 3.);
    //p.xz *= Rot(PI / (4. + 2. * sin(iTime))); // normally PI / 2.
    //p += shift;
    //fractal = min(fractal, sdStar(p) * pow(scale, (float(i + 1))));

      float r0, r1, r2, r3, r4;
      r0 = sdStar(p);
      r1 = r2 = r3 = r4 = 1e10;

      vec3 p1 = p;
      p1 += shift;
      p1 /= scale;
      p1.xz *= Rot(PI / 3.);
      r1 = sdStar(p1);
      //p.x = abs(p.x);
      //p.xy *= Rot(PI / 3.);
      //p.xy *= Rot(PI / 3.);
      //p.x = abs(p.x);
      vec3 p2 = p;
      p2.xy *= Rot(1.9106329);
      p2 += shift;
      p2 /= scale;
      r2 = sdStar(p2);
      //p.xz *= Rot(1.9106329);
      vec3 p3 = p;
      p3.xz *= Rot(2. * PI / 3.);
      p3.xy *= Rot(1.9106329);
      p3 += shift;
      p3 /= scale;
      r3 = sdStar(p3);
      vec3 p4 = p;
      p4.xz *= Rot(- 2. * PI / 3.);
      p4.xy *= Rot(1.9106329);
      p4 += shift;
      p4 /= scale;
      r4 = sdStar(p4);
      fractal = min(min(min(r0, r1 * scale), r2 * scale),min(r3 * scale, r4 * scale));
  }
  return vec2(mix(fractal, sphere, 0.5 + 0.5 * sin(iTime * 1.)), 0.6);
  //return vec2(sdStar(p), 0.6);
}




