mat2 r2d(float a){float cosa = cos(a);float sina = sin(a);return mat2(cosa,sina,-sina,cosa);}

float sat(float a)
{
  return clamp(a,0.,1.);
}

float cir(vec2 p,float r)
{
  return length(p)-r;
}

float sub(float a, float b)
{
  return max(a,-b);
}

vec3 rdr(vec2 uv)
{
  uv*= r2d(1.);
  vec3 col;

  float stars= texture(iChannel0,uv*2.).x;
  col = vec3(stars)*float(stars>.97);
  vec2 ruv = uv*vec2(.8,3.7);
  vec2 rp = ruv-vec2(0.,.08);
  float ring = sub(cir(ruv, .6),cir(rp,.35));


  float ringOp = mix(.2,1.,sat(abs(uv.x*(sat(uv.y*10.)+.3))-.3));
  float back = sub(ring, uv.y);
  float front = sub(ring,-uv.y);
  float ringPatt = sat(.5*sin(2.+200.*length(rp))+sin(1.+300.*length(rp)));
  col = mix(col,vec3(ringOp),(1.-sat(back*200.))*ringPatt);
  float planet = cir(uv,.3);
  col = mix(col,vec3(.45,.34,.56)*(.3+1.-sat(length(2.*uv-vec2(-.1,.1)*5.))),1.-sat(planet*200.));
  col = mix(col,vec3(ringOp),(1.-sat(front*200.))*ringPatt);
  return col;
}
vec3 grid(vec2 uv)
{
  vec2 r = clamp(mod(uv,vec2(.25))-vec2(.001),vec2(0.),vec2(1.));
  return vec3(.5)*(1.-sat(200.*(min(r.x,r.y))))*(.5+.5*sin(uv.y*500.))
  *(.5+.5*sin(uv.x*500.))*(sat(length(uv)-.4));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy-.5*iResolution.xy) / iResolution.xx;

  uv *=vec2(-1.,1.)*2.3;
  uv -= vec2(.15, -0.07);
  vec3 col = rdr(uv);
  float sz = 0.1;

  vec3 col2 = rdr(vec2(float(int(uv.x/sz)),float(int(uv.y/sz)))*sz);

 col= mix(col,col2, sat(length(uv)*float(length(uv) < 1.)));
  col+= 1.2*vec3(.34,.56,.87)*sat(1.-length(1.*uv-vec2(-.1,.1)*2.));
col+= .5*vec3(.34,.56,.87).zyx*sat(1.-length(3.*uv-vec2(-.1,.1)*2.));

col += texture(iChannel0,uv+iTime*50.).xxx*.1;
col += grid(uv);
  fragColor = vec4(col, 1.0);
}

vec2 rot(vec2 p, vec2 pivot, float angle) {
	float c = cos(angle);
	float s = sin(angle);

	p -= pivot;
	p = vec2(p.x * c - p.y * s, p.x * s + p.y * c);
	p += pivot;

	return p;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy )/iResolution.y;


    vec2 m = iMouse.xy / iResolution.xy;
    float angle = mix( -3.1415 , 3.1415 , m.x );
    float scale = mix( 3.0f , 0.1f , m.y );


    vec2 c = /* _Area.xy + */ (uv ) *scale;
    c = rot(c, vec2(0), angle);



    vec2 z;

    float iter;
    for (iter = 0.0; iter < 255.0; iter++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;

        if (length(z) > 2.0)
            break;
    }


    // Output to screen
    fragColor = vec4( vec3(iter / 255.0f) ,1.0);
}

void mainImage( out vec4 O, vec2 u )
{ 
    vec2  R = iResolution.xy,
          U = u - .5 *R;

    float l = length(U)/R.y,
          a = atan(U.x , U.y ),
          s = 8.*( a/6.2831  + iTime*.1 + l*2. );

    float m = min( fract(s) , fract(1.-s) ),
          v = m * .3 + .1 - l;

    O = vec4(smoothstep( -.7, .7 , v/fwidth(v) ) );	
}

float remap01( float a , float b , float t ) {
	return ( t - a ) / ( b - a );   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - 0.5 * iResolution.xy ) / iResolution.y;


    vec3 col =vec3(0);


    vec3 ro = vec3(0);
    vec3 rd = normalize( vec3( uv.x , uv.y , 1 ) );

    vec3 s = vec3( 0 , 0 , 3);

    float radius = 1.0;
    float t = dot( s - ro , rd );
    vec3 p = ro + rd * t ;


    float y = length( s - p );

    if ( y < radius ) {
    	float x = sqrt( radius * radius - y * y  );
    	float t1 = t - x ;
    	float t2 = t + x;

        float c = remap01(s.z , s.z  - radius, t1 );
        col = vec3(c);
    }





    fragColor = vec4(col,1.0);
}

