float pixSize = 15.;

#define lum(pix) dot( pix, vec4(.3,.59,.11,0) )  //luminance
#define T(x,y)   texture( iChannel0, uv2 + vec2(x,y) )        

void mainImage( out vec4 O,  vec2 u ) {

    vec2 R = iResolution.xy,
         U = u / pixSize,
       div = pixSize / R,
       uv2 = floor(U)*div,
      diff = fract(U);

    //checkerboard pattern : half of the pixels are not changed
    if (fract( dot(floor(U),vec2(.5)) ) < .5) 
        { O = T(0,0); return; }

    // neighbors
	vec4[4] pix = vec4[4]( T( 0,     div.y),
                           T( div.x, 0    ),
                           T( 0,    -div.y),
                           T(-div.x, 0    ) );

    //where is the biggest contrast ?   
    int comp = int (  abs( lum(pix[0]) - lum(pix[2]) )
                    > abs( lum(pix[1]) - lum(pix[3]) ) );
    vec2   d = abs(diff-.5) - vec2(1-comp,comp); // offset = 0,1 or 1,0
    ivec2  v = ivec2( vec2(3.5,2.5) - diff*2. );

    O = dot(d,d) < .5 
        ? pix[ v[comp] ]                                 // 2 circles on the borders
        : mix( pix[comp+2], pix[comp] , diff[1-comp] );  // a gradient in between        
}

#define ditherThreshold iMouse.x / iResolution.x

// https://coolors.co/
const int colorsSize = 7;
vec3 colors[colorsSize] = vec3[](
    // trans
    vec3(91, 206, 250),
    vec3(245, 169, 184),
    vec3(255, 255, 255),
    // enby
    vec3(240, 183, 25),
    vec3(252, 236, 217),
    vec3(181, 84, 192),
    vec3(56, 41, 36)
);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    for (int i = 0; i < colorsSize; i++) {
        colors[i] = colors[i] / 255.;
        //colors[i] = colors[i] * (sin(iTime + float(i)) * 0.5 + 1.0);
    }

    vec2 uv = fragCoord/iResolution.xy;
	vec3 col = texture(iChannel0, uv).rgb;

    // comparasion
    float mDist[colorsSize];
    float minDist = 9999.9;
    int mIndex = 0;
    int mOldIndex;

    for (int i = 0; i < colorsSize; i++) {
        vec3 compareCol = colors[i];

        vec3 distVec = abs(col - compareCol);
        float dist = (distVec.r + distVec.g + distVec.b) / 3.0;

        mDist[i] = dist;
        if (dist < minDist) {
            mOldIndex = mIndex; // terrible
            mIndex = i;
            minDist = dist;
        }
    }

    // dithering (worst possible way to do it)

    vec3 col1 = colors[mIndex];
    vec3 col2 = colors[mOldIndex];
    float dist1 = mDist[mIndex];
    float dist2 = mDist[mOldIndex];

    col = col1;
    if ((dist2 - dist1) <= ditherThreshold && mod(fragCoord.x + fragCoord.y, 2.) == 0.) col = col2;

    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define EPSILON 0.0001
#define PI 3.14159265


mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
	return mat2(c, -s, s, c);
}

float sdSphere(vec3 p, float radius) { return length(p) - radius; }
float sdBox( vec3 p, vec3 b ) { vec3 q = abs(p) - b; return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0); }




float sdCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}


// ↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓

vec2 getDist(vec3 p) {

    //p.x += -0.8;
    p.xz *= Rot(iTime * 2.);
    float scale = 0.4 + 0.1 * sin(iTime);
	vec2 box = vec2(1e10, 1.);

    for (float i = 0.; i < 5.; i++) {
        vec2 box2 = vec2( sdBox(p, vec3(1)) * pow(scale, i), 0.5 + i / 5. );
        box = box.x < box2.x ? box : box2;
        p = abs(p);
        p -= 1.;
	    p.xz *= Rot(iTime);
        p /= scale;
    }

    return box;
}




// ↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑





vec2 rayMarch(vec3 ro, vec3 rd) {
	float d = 0.;
    float info = 0.;
    //float glow = 0.;
    float distToClosestLight = 9999999.;
    for (int i = 0; i < MAX_STEPS; i++) {
    	vec2 distToClosest = getDist(ro + rd * d);
        d += distToClosest.x;
        info = distToClosest.y;
        if(abs(distToClosest.x) < EPSILON || d > MAX_DIST) {
        	break;
        }
    }
    return vec2(d, info);
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(EPSILON, 0.);
    vec3 n = getDist(p).x - vec3(getDist(p - e.xyy).x,
                               getDist(p - e.yxy).x,
                               getDist(p - e.yyx).x);
	return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    // ray origin
    vec3 ro = vec3(0, 0., -5.5);
    float zoom = 1.100;

    // ray direction
    vec3 rd = normalize(vec3(uv, zoom));

    vec2 rm = rayMarch(ro, rd);
    float d = rm[0];
    float info = rm[1];

    float color_bw = 0.;
    vec3 color = vec3(0.);
    if (d < MAX_DIST) {
        vec3 n = getNormal(ro + rd * d);
        n.zy *= Rot(iTime);
    	color = vec3( n + 1.0 );
        color *= info;
        //color_bw += 0.5 + dot(n, normalize(vec3(1,1,0))) / 2.;
    }
    //color = vec3( color_bw );



    fragColor = vec4(color,1.0);
}

