            float b = clamp(0.5 + 0.5*dot(n, normalize(vec3(1,1,1))), 0.,1.);
            float K = 1. - pow(max(dot(n,rd),0.), 4.);
            col.xyz = 1.*albedo*colB + 0.3*colA*K;
        }
        else
