}

float DRoundBox(vec3 p,vec3 s,float r)
{
    float d = length(p-vec3(clamp(p.x,-s.x+r,s.x-r),clamp(p.y,-s.y+r,s.y-r),clamp(p.z,-s.z+r,s.z-r)))-r;
