vec3 GetNormal(vec3 p)
{
    vec2 e = vec2(.01, 0);
    float d = GetDist(p);
    vec3 n = vec3(
    	d-GetDist(p-e.xyy),
        d-GetDist(p-e.yxy),
        d-GetDist(p-e.yyx)
    );

    return normalize(n);
}

float RayMarcher(vec3 pos,vec3 dir)
{
	vec3 m_pos = pos;
    float dist=0.0;
    for(int i=0;i<MAX_STEPS;i++)
    {
        float d = GetDist(m_pos);
        m_pos += dir*d;
        dist+=d;
        if( d<=MIN_DIST || abs(dist)>=MAX_DIST)break;

    }
	return clamp(dist,0.0,MAX_DIST);
}
vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 sphere_colision(vec3 pos,float r,Ray ray)
{
    vec3 spro = vec3(pos-ray.ro);
    float t = dot(spro,ray.rd);
    vec3 p = ray.ro+ray.rd*t;

    float y = length(pos-p);
    if(y>r) return vec3(1)*MAX_DIST;
    float x = sqrt(r*r-y*y);
    float t1 = t-x;
    float t2 = t+x;
    return vec3(t1,t,t2);
}

float RayTracer(vec3 ro,vec3 rd)
{
    Ray ray = Ray(ro,rd);
    vec3 sphere = sphere_colision(vec3(0,0,0),1.,ray);


    float d = sphere.x;
	return d;
}

