float scene(vec3 p, float t) {
    float d = 1000.0;

    p = vec3(rot(p.xz, t), p.y).xzy;
    p = vec3(rot(p.xy, t), p.z).xyz;

    d = min(d, sdBox(p, vec3(0.5) / (1.0 - cos(t / 4.0 * PI) * 0.5)));

    return d;
}

vec3 trace(vec2 uv, float t) {
    vec3 p = vec3((uv.xy * 2.0 - 1.0)* iResolution.xy / iResolution.yy, -2.0);
    vec3 d = normalize(vec3((uv.xy * 2.0 - 1.0)* iResolution.xy / iResolution.yy, 2.0));

    for (int i=0; i<16; i++) {
    	p += scene(p, t) * d;   
    }

    vec3 color = texture(iChannel1, p.xy).rgb;
    color*= step(-0.01, -scene(p, t));

    return color;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord){
    float t = iTime / 60.0 * BPM;
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color_bg = abs(fract(uv.xyy + t / 8.0) * 2.0 - 1.0);
    color_bg = vec3(fract(color_bg.x / 1.312), fract(color_bg.y / 2.0  + color_bg.x / 2.0), fract(-color_bg.x * color_bg.y));
    color_bg = texture(iChannel2, uv.xy / 10.0 + color_bg.xy).rgb;

    vec3 color = color_bg;

    vec3 color_scene = trace(uv, t);

    color = mix(color, color_scene, step(0.01,length(color_scene)));

    color_bg = texture(iChannel2, uv.xy).rgb;
    color = clamp(color, color_bg - 16.0 / 255.0, color_bg + 16.0 / 255.0);

    fragColor = vec4(color, 1.0);
}

