    d = min(d, sdBox(p - vec3(+2.0, 1.0 - pow(abs(fract(t / 4.0) * 2.0 - 1.0), 3.0), 0.0), vec3(0.5, 0.75, 0.125)));
    d = min(d, sdBox(p - vec3(-1.0, 1.0 - pow(abs(fract(t * 1.0) * 2.0 - 1.0), 3.0), 0.0), vec3(0.25, 0.75, 0.125)));
    d = min(d, sdBox(p - vec3(+1.0, 1.0 - pow(abs(fract(t * 2.0) * 2.0 - 1.0), 3.0), 0.0), vec3(0.125, 1.0, 0.125)));

    p = vec3(rot(p.xz, t), p.y).xzy;
    p = vec3(rot(p.xy, t), p.z).xyz;

    d = min(d, sdBox(p, vec3(0.5) / (1.0 - cos(t / 4.0 * PI) * 0.5)));

