// ##### COMMON VALUES #####

// Change these Parameters to Your Liking!
// Maximum Number of Marches,
// You want it to limit the raymarcher before the max distance parameter or it will look bad.
#define MAX_MARCHES 64

// Redundant for this idk if the max marches are large and you see ugly stuff then increase this
#define MAX_DISTANCE 6.5

// fBm Number of Octaves (Detail)
#define NUM_OCTAVES 8

// Size of Steps, smaller means more sampling over depth but also means more computation.
// Increase max marches if the scene goes invisible.
#define STEP_SIZE 0.1

// If you march less rays, the nebula will appear darker. Bump this up to make it brighter again.
#define DENSITY 0.1

// Tiles, more means easier on the Hardware
#define TILES 5
// So kindly made by LoicVDB uwu

// Amount of distortion to apply
#define DISTORT_AMOUNT 2.5

// Offset the 4D Noise, Basically a different Nebula
#define W_OFFSET 0.5

// Difference in offset between color channels
#define W_DIFFERENCE 0.01

// Oof ugly mess below watch out lol

// ##### NOISE #####

// Simplex 4D Noise
// by Ian McEwan, Ashima Arts
// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83#simplex-noise
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
float permute(float x){return floor(mod(((x*34.0)+1.0)*x, 289.0));}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}
float taylorInvSqrt(float r){return 1.79284291400159 - 0.85373472095314 * r;}
vec4 grad4(float j, vec4 ip){
  const vec4 ones = vec4(1.0, 1.0, 1.0, -1.0);
  vec4 p,s;
  p.xyz = floor( fract (vec3(j) * ip.xyz) * 7.0) * ip.z - 1.0;
  p.w = 1.5 - dot(abs(p.xyz), ones.xyz);
  s = vec4(lessThan(p, vec4(0.0)));
  p.xyz = p.xyz + (s.xyz*2.0 - 1.0) * s.www; 
  return p;
}
float snoise(vec4 v){
  const vec2  C = vec2( 0.138196601125010504,
                        0.309016994374947451);
  vec4 i  = floor(v + dot(v, C.yyyy) );
  vec4 x0 = v -   i + dot(i, C.xxxx);
  vec4 i0;
  vec3 isX = step( x0.yzw, x0.xxx );
  vec3 isYZ = step( x0.zww, x0.yyz );
  i0.x = isX.x + isX.y + isX.z;
  i0.yzw = 1.0 - isX;
  i0.y += isYZ.x + isYZ.y;
  i0.zw += 1.0 - isYZ.xy;
  i0.z += isYZ.z;
  i0.w += 1.0 - isYZ.z;
  vec4 i3 = clamp( i0, 0.0, 1.0 );
  vec4 i2 = clamp( i0-1.0, 0.0, 1.0 );
  vec4 i1 = clamp( i0-2.0, 0.0, 1.0 );
  vec4 x1 = x0 - i1 + 1.0 * C.xxxx;
  vec4 x2 = x0 - i2 + 2.0 * C.xxxx;
  vec4 x3 = x0 - i3 + 3.0 * C.xxxx;
  vec4 x4 = x0 - 1.0 + 4.0 * C.xxxx;
  i = mod(i, 289.0); 
  float j0 = permute( permute( permute( permute(i.w) + i.z) + i.y) + i.x);
  vec4 j1 = permute( permute( permute( permute (
             i.w + vec4(i1.w, i2.w, i3.w, 1.0 ))
           + i.z + vec4(i1.z, i2.z, i3.z, 1.0 ))
           + i.y + vec4(i1.y, i2.y, i3.y, 1.0 ))
           + i.x + vec4(i1.x, i2.x, i3.x, 1.0 ));
  vec4 ip = vec4(1.0/294.0, 1.0/49.0, 1.0/7.0, 0.0) ;
  vec4 p0 = grad4(j0,   ip);
  vec4 p1 = grad4(j1.x, ip);
  vec4 p2 = grad4(j1.y, ip);
  vec4 p3 = grad4(j1.z, ip);
  vec4 p4 = grad4(j1.w, ip);
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;
  p4 *= taylorInvSqrt(dot(p4,p4));
  vec3 m0 = max(0.6 - vec3(dot(x0,x0), dot(x1,x1), dot(x2,x2)), 0.0);
  vec2 m1 = max(0.6 - vec2(dot(x3,x3), dot(x4,x4)            ), 0.0);
  m0 = m0 * m0;
  m1 = m1 * m1;
  return 49.0 * ( dot(m0*m0, vec3( dot( p0, x0 ), dot( p1, x1 ), dot( p2, x2 )))
               + dot(m1*m1, vec2( dot( p3, x3 ), dot( p4, x4 ) ) ) ) ;
}

// 2D Noise for Dithering
float hash13(vec3 p3){
    p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

// fBm Noise
float fbm(vec4 x){
    float v = 0.0;
    float a = 0.5;
    for (int i = 0; i < NUM_OCTAVES; ++i){
        v += a * snoise(x);
        x = x * 2.0;
        a *= 0.5;
    }
    return v;
}

// 4D Nebula Function
float distort(vec3 raypos, float variance){
    float distort = 0.0;
    distort = distort+(DISTORT_AMOUNT*fbm(vec4(raypos, variance)));
    return distort;
}

float nebulanoise(vec3 raypos, float variance, float base){
    float density = clamp((fbm(vec4((raypos)+distort(raypos, variance), base))-0.25)*8192.0, 0.0, 1.0)/(pow(distance(vec3(0.0), raypos),4.0)*2.0);
    return density;
}

// ##### RAYMARCHING #####

// Compute/March the Ray
vec3 raymarch(vec3 camerapos, vec3 raydir, vec2 coord){
    float distorigin=0.0;
    float densityaddr=0.0;
    float densityaddg=0.0;
    float densityaddb=0.0;
    vec3 density=vec3(0.0);
    vec3 raypos = camerapos;
    vec3 raydirmod = (raydir*(hash13(vec3(coord, iFrame))+1.0))*STEP_SIZE;
    for(int i=0; i<MAX_MARCHES; i++) {
        raypos = raypos + raydirmod;
        densityaddr = nebulanoise(raypos/4.0,  W_OFFSET-W_DIFFERENCE, W_OFFSET)*DENSITY;
        densityaddg = nebulanoise(raypos/4.0,  W_OFFSET, W_OFFSET)*DENSITY*0.5;
        densityaddb = nebulanoise(raypos/4.0,  W_OFFSET+W_DIFFERENCE, W_OFFSET)*DENSITY*0.25;
        density = vec3(density.r+densityaddr, density.g+densityaddg, density.b+densityaddb);
        distorigin = raypos.z-camerapos.z;
        if(distorigin>MAX_DISTANCE) break;
    }
    return density;
}

// ##### RENDERING #####

// Render the Image
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // LoicVDB: I store the number of samples in the alpha channel
    // LoicVDB: take the previous color and sample count (black and 0 if on first frame)
    vec4 oldFragColor = vec4(0.);
    if(iFrame != 0) oldFragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // LoicVDB:I put it in the fragColor var because I want to keep it even if it's not in a tile
    fragColor = oldFragColor;

    // LoicVDB's Hyper Epic Tiles Function
    int index = int(fragCoord.x/iResolution.x*float(TILES))
              + int(fragCoord.y/iResolution.y*float(TILES))*TILES;
    if(iFrame-(TILES*TILES*(iFrame/(TILES*TILES))) != index) return;

    // Camera Orientation
    vec3 xdir = vec3(1.0,0.0,0.0);
    vec3 ydir = vec3(0.0,1.0,0.0);
    vec3 zdir = vec3(0.0,0.0,1.0);
    float FOV = 1.0;
    vec3 camerapos = vec3(0.0, 0.0, -8.0);

    // Undistorted Normalized Pixel Coordinates (From 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.x;
    vec2 uvd = fragCoord/iResolution.xy;
    vec3 raydir = normalize(FOV*(uv.x*xdir + uv.y*ydir) + zdir);
    vec3 raymarched = raymarch(camerapos, raydir, vec2(fragCoord));

    // LoicVDB: add the color and increase the sample count
    fragColor += vec4(raymarched, 1.0);
}

