vec3 acesFilm(const vec3 x){
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x*(a*x+b))/(x*(c*x+d)+e),0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // LoicVDB: take the color from the A buffer
    vec4 texel = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // LoicVDB: divide it by the sample count (the alpha channel)
    vec3 color = (texel.a == 0. ? vec3(0.) : texel.rgb / texel.a);

    // Output to screen
    fragColor = vec4(acesFilm(color), 1.0);
}

