      length(max(vec4(p.x,q.y,q.z,q.w),0.0))+min(max(p.x,max(q.y,max(q.z, q.w))),0.0),
      length(max(vec4(q.x,p.y,q.z,q.w),0.0))+min(max(q.x,max(p.y,max(q.z, q.w))),0.0)),
      length(max(vec4(q.x,q.y,p.z,q.w),0.0))+min(max(q.x,max(q.y,max(p.z, q.w))),0.0)),
      length(max(vec4(q.x,q.y,q.z,p.w),0.0))+min(max(q.x,max(q.y,max(q.z, p.w))),0.0));
}


float map( in vec4 pos )
{
    vec3 m = 3.14 * iMouse.xyz/iResolution.xyx;
    pos = inverse(rotYU(pos, m.x)) * pos;
    pos = inverse(rotZU(pos, m.y)) * pos;
    //pos = rotXZ(pos, sin(iTime));
    //pos = rotZU(pos, sin(iTime));
    float d = 9999.;
    d = min(d, sdBoundingBox(pos+vec4(0.8, 0,0,0), vec4(0.2, 0.2, 0.2, 0.2), 0.02 ));
    d = min(d, sdBox(pos, vec4(0.2, 0.2, 0.2, 0.2) ));
    d = min(d, sdSphere(pos-vec4(.8, 0,0,0), 0.2 ));
    return d;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec4 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( vec4(pos.xyz + e.xyy*eps, pos.w) ) + 
					  e.yyx*map( vec4(pos.xyz + e.yyx*eps, pos.w) ) + 
					  e.yxy*map( vec4(pos.xyz + e.yxy*eps, pos.w) ) + 
					  e.xxx*map( vec4(pos.xyz + e.xxx*eps, pos.w) ) );
}

#define AA 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera movement	
	float an = 0.5*(iTime-10.0);
    //an = 0.;
	vec3 ro = vec3( 1.0*sin(an)-0.4, 0.4, 1.0*cos(an)+0.2 );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 3.0;
        float t = 0.0;
        float z = sin(iTime/2.)/5.;
        vec4 pos = vec4(ro + t*rd, z);
        for( int i=0; i<256; i++ )
        {
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
            pos.xyz += h*rd;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.57703)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

