// Raycast to the map, returns 0 if not cast.
int MapRaycast(in vec3 Orig, in vec3 Ray, in float MaxDist, out vec3 CastPos, out float CastDist, out float MinStepLen)
{
    float RayHorz = length(Ray.xz);
    if(RayHorz <= Epsilon)
    {
        CastPos.y = Altitude(texture(iChannel0, Orig.xz / MapScale));
        CastDist = Orig.y - CastPos.y;
        return 1;
    }

    float RayK = -Ray.y / RayHorz;
    CastDist = 0.0;
    MinStepLen = 100000.0;

    // Move the orig forward the ray direction and check if it's near enough to the terrain
    for(int s = 0; s < MaxIterSteps; s++)
    {
        float StepLength = 0.0;
        float Alt = Altitude(texture(iChannel0, Orig.xz / MapScale));
    	float MapK = texture(iChannel1, Orig.xz / MapScale).z * length(iResolution.xy) / MapScale;
    	float K = RayK + MapK;

        // The ray is going away from the map
        if(RayK < 0.0 && -RayK > MapK)
        {
            return 0;
        }

        // Calculate the step length to move the orig forward
        if(K > Epsilon)
        {
            StepLength = (Orig.y - Alt) / RayHorz / K;
            StepLength = max(StepLength * StepLengthModifier, MinDistThreshold);
            MinStepLen = min(MinStepLen, StepLength);
        }
        else
        {
            // Infinite step length, the ray is parallel to the map plane and it would never cast to the terrain
            return 0;
        }

        // Do the move
        Orig += Ray * StepLength;
        CastDist += StepLength;

        // Check if near enough
        if(Orig.y <= Alt + MinDistThreshold)
        {
			if(StepLength <= MinDistThreshold) break;
            else
            {
                StepLength *= -0.5;
                Orig += Ray * StepLength;
                CastDist += StepLength;
            }
        }

        // Check if far enough to give up
        if(CastDist > MaxDist)
        {
            CastDist = MaxDist;
            return 0;
        }
    }

    CastPos = Orig;
    return 1;
}

// Get the normal from the altitude map
vec3 GetNormal(in vec2 OrigXZ)
{
    float Alt1 = Altitude(texture(iChannel0, (OrigXZ - vec2(1.0, 0.0)) / MapScale));
    float Alt2 = Altitude(texture(iChannel0, (OrigXZ + vec2(1.0, 0.0)) / MapScale));
    float Alt3 = Altitude(texture(iChannel0, (OrigXZ - vec2(0.0, 1.0)) / MapScale));
    float Alt4 = Altitude(texture(iChannel0, (OrigXZ + vec2(0.0, 1.0)) / MapScale));
    return normalize(vec3(Alt1 - Alt2, 2.0, Alt3 - Alt4));
}

// Render the scene
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // The rotation of the camera
    float PitchAngle = Pi * 0.33 + Pi * 0.05 * sin(Pi * 0.06 * iTime);
    float YawAngle = Pi * 0.04 * iTime;
    mat3 RayRotYaw = mat3
    (
        vec3(cos(YawAngle), 0.0, -sin(YawAngle)),
        vec3(0.0, 1.0, 0.0),
        vec3(sin(YawAngle), 0.0, cos(YawAngle))
    );
    mat3 RayRotPitch = mat3
    (
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, cos(PitchAngle), -sin(PitchAngle)),
        vec3(0.0, sin(PitchAngle), cos(PitchAngle))
    );

    // Create rays from the screen pixels
    vec3 StartRay = vec3(fragCoord / iResolution.xy - 0.5, 0.5);
    StartRay.x *= iResolution.x / iResolution.y;
    StartRay *= RayRotPitch;
    StartRay *= RayRotYaw;
    StartRay = normalize(StartRay);

    // Prepare raycast to the scene
    vec3 Orig = vec3(5.0 + sin(iTime * Pi * 0.1) * 50.0, CameraAlt, 5.0 + cos(iTime * Pi * 0.1) * 50.0);
    float CamPosAlt = Altitude(texture(iChannel0, Orig.xz / MapScale));
    Orig.y = max(Orig.y, CamPosAlt + 5.0);
    vec3 Ray = StartRay;
    vec3 CastPos = Orig;
    float CastDist = 0.0;
    float MinStepLen;
    vec3 Diffuse = FogColor;
    vec3 Specular = vec3(0);
    vec3 NLD = normalize(LightDir);
    vec3 Color = FogColor;

    // Begin raycast to the scene
    if(MapRaycast(Orig, Ray, MaxDOV, CastPos, CastDist, MinStepLen) != 0)
    {
        // Cast color
        vec3 CastColor = texture(iChannel3, CastPos.xz / MapScale).xyz;
        vec3 MetallicColor = texture(iChannel2, CastPos.xz / MapScale).xyz;

        // Cast normal
        vec3 Normal = GetNormal(CastPos.xz);
        float NdL = dot(Normal, -NLD);
        Diffuse = vec3(max(NdL, 0.0));

        vec3 Halfway = -normalize(Ray + NLD);
        Specular = vec3(1.0) * min(pow(max(dot(Normal, Halfway), 0.0), TerrainShineness), 1.0);

        // Add some shadow
        if(NdL > 0.0)
        {
            float Shadow = 0.0;
            float ShadowDist;
            vec3 ShadowCastPos;
            if(MapRaycast(CastPos + (Normal - NLD) * 2.0 * MinDistThreshold, -NLD, MaxDOV, ShadowCastPos, ShadowDist, MinStepLen) != 0)
            {
                // Shadowed
                Shadow = 1.0; // clamp(ShadowDist * 0.5 - MinDistThreshold * 1.5 + NdL * 10.0, 0.0, 1.0);
            }
            else
            {
                // Soft shadow
                // I don't know how to implement soft shadow.
                // So there's no soft shadow.
                // Sorry.
            }
            Diffuse *= 1.0 - Shadow;
            Specular *= 1.0 - Shadow;
        }
        // Add some fog
        Color = mix((Diffuse + Ambient) * CastColor + Specular * MetallicColor, FogColor, CastDist / MaxDOV);
    }

    fragColor = vec4(Color, 1.0);
}

