// Higher for precise map raycasting and more adaptive to the dynamic changing altitude map (e.g. Video as the altitude map)
// Lower for more FPS. K-map generation is slower.
// For the static altitude map (A texture, rather than a video), change SEARCH_RADIUS to 1
#define SEARCH_RADIUS 6

const int SearchRadius = SEARCH_RADIUS;

// K-map generation
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float MaxK = 0.5;
    vec2 MaxKPos = fragCoord;
    float ThisAlt = Altitude(texture(iChannel0, fragCoord / iResolution.xy));

    // First, calculate the K-value from nearby pixels
    const int SR = SearchRadius;
    for(int y = -SR; y <= SR; y++)
    {
    	for(int x = -SR; x <= SR; x++)
        {
            if(x == 0 && y == 0) continue;
            vec2 xy = vec2(x, y);
            vec2 CurPosition = (fragCoord + vec2(xy));

            float CurAltDiff = Altitude(texture(iChannel0, CurPosition / iResolution.xy)) - ThisAlt;
            float CurDist = length(xy);

            // Ratio of height difference to distance, is the K-value
            float K = CurAltDiff / CurDist;
            if(K > MaxK)
            {
                MaxK = K;
                MaxKPos = CurPosition;
            }
        }
    }

    // Then, collect the previous K-value result from the nearby pixels, expand the highest ratio to all the map frame by frame.
    for(int y = -SR; y <= SR; y++)
    {
    	for(int x = -SR; x <= SR; x++)
        {
            if(x == 0 && y == 0) continue;
            vec2 xy = vec2(x, y);
            vec2 CurPosition = (fragCoord + vec2(xy));
            // Nearby pixel K-value and the corresponding coordinate
            vec4 Data = texture(iChannel1, CurPosition / iResolution.xy);

            vec2 Position = Data.xy;
            float AltDiff = Altitude(texture(iChannel0, Position / iResolution.xy)) - ThisAlt;
            float Dist = distance(fragCoord, Position);
            if(Dist > 0.5) // Not grabbing current data back from the nearby
            {
                float K = AltDiff / Dist;
                if(K > MaxK)
                {
                    MaxK = K;
                    MaxKPos = Position;
                }
            }
        }
    }

    // Output the K-value map
    fragColor = vec4(MaxKPos, MaxK, 1.0);
}

