#define EPS .000002

#define AA 4

// uncomment this to enable backface culling
// #define CULL_BACKFACE

const vec3 cameraPosition = vec3(0., 2., 12.);

struct Ray
{
	vec3 origin, direction;  
};

vec2 hash21(uint q)
{
	uvec2 n = q * uvec2(1597334673U, 3812015801U);
	n = (n.x ^ n.y) * uvec2(1597334673U, 3812015801U);
	return vec2(n) * (1. / float(0xffffffffU));
}

mat3 getCameraMatrix(vec3 origin, vec3 target)
{
    vec3 lookAt = normalize(target - origin);
    vec3 right = normalize(cross(lookAt, vec3(0., 1., 0.)));
    vec3 up = normalize(cross(right, lookAt));
    return mat3(right, up, lookAt);
}

Ray getCameraRay(vec2 uv)
{
    vec3 origin = cameraPosition;
    vec3 target = vec3(0., 1., 0.);
    mat3 camera = getCameraMatrix(origin, target);
    vec3 direction = normalize(camera * vec3(uv, 2.));
    return Ray(origin, direction);
}

/**
Tomas Möller & Ben Trumbore (1997) Fast, Minimum Storage Ray-Triangle Intersection,
Journal of Graphics Tools
https://cadxfem.org/inf/Fast%20MinimumStorage%20RayTriangle%20Intersection.pdf
*/
vec3 rayTriangleIntersect(Ray ray, vec3 A, vec3 B, vec3 C)
{
	vec3 result = vec3(-1.);
    vec3 e1 = B - A; vec3 e2 = C - A;
    vec3 dxe2 = cross(ray.direction, e2);
    float det = dot(dxe2, e1);
    // ray parallel to triangle plane
	if (abs(det) < EPS
#ifdef CULL_BACKFACE
	// triangle facing away from camera (with vertices wound counter-clockwise)
	|| det < EPS
#endif
	) return result;
    float invDet = 1. / det;
    vec3 t = ray.origin - A;
    float u = dot(dxe2, t) * invDet;
    vec3 txe1 = cross(t, e1);
    float v = dot(txe1, ray.direction) * invDet;
    if (u < 0. || v < 0. || u + v > 1.) return result;
    float d = dot(txe1, e2) * invDet;
    result = vec3(d, u, v);
    return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = vec3(0.);
    int aaSamples = AA * AA;
    for (int i = 0; i < aaSamples; ++i)
    {
        // Find x & y subpixel jitter locations
        float x = mod(float(i), float(AA));
        float y = mod(float(i / AA), float(AA));

        // Subpixel jitter
        vec2 jitter = (hash21(uint(i)) + vec2(x, y)) / float(AA);

        vec2 uv = (2. * (fragCoord + jitter) - iResolution.xy) / iResolution.y;

        Ray ray = getCameraRay(uv);

        float t = iTime * .75;

        vec3 tri = rayTriangleIntersect(ray, vec3(0., 4. * sin(t), 2. * cos(t)), 
                                             vec3(-3. * cos(t), -3. * sin(t), -2.), 
                                             vec3(3. * cos(t), -1., -6. * sin(t)));

        // Color the triangle using barycentric weights
        if (tri.x > 0.)
			col += vec3(tri.yz, 1. - tri.y - tri.z);
    }

    fragColor = vec4(pow(col/float(aaSamples), vec3(.4545)), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time increase from 0 to 2
    float time = mod(iTime * 0.5, 2.0);

    // Dynamic texture pixel offset
    vec2 offset = vec2(0.5) - uv;
    offset /= time;
    offset *= length(offset);

    // Getting texture pixel
    vec2 texCoord = uv + offset;
    vec4 col = texture(iChannel0, texCoord);

    // Output to screen
    fragColor = col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;
    col*= mix(1.1, 0.975, step(2.0, mod(fragCoord.y, 4.0)));

    fragColor = vec4(col, 1.0);
}

