float in_box(vec2 p) {
    return (smoothstep(-0.04, 0.0, p.x) * (1.0 - smoothstep(1.0, 1.04, p.x))) * (smoothstep(-0.04, 0.0, p.y) * (1.0 - smoothstep(1.0, 1.04, p.y))); 
}


float scene (vec3 p, float t) {
    float d = 10000.0;

    d = min(d, 1.0 - length(p.xy) - (0.01 - 0.2 * max(0.0, .95 + cos(1.0 / 4.0 * t * 3.141592))) * texture(iChannel3, vec2(8.0 * atan(p.y, p.x) / 3.141592, p.z / 4.0)).r);


    return d;
}


vec3 trace(vec2 p, float t) {
    vec3 o = vec3(p.x, p.y, t / 4.0);
    vec3 d = normalize(vec3(p, 4.0));


    for (int i = 0; i < 32; i++) {
     	o += d * scene(o, t);
    }


    vec3 col = texture(iChannel3, vec2(8.0 * atan(o.y, o.x) / 3.141592, o.z / 4.0)).rgb;
    col *= max(0.0, 8.0 - (o.z - t / 4.0)) / 8.0;

 	return col;   

}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    float t = iTime * 127.0 / 60.0;
    vec2 uv = fragCoord/iResolution.xy;

    vec2 tv_center = 1.0 * vec2(cos(1.0 / 19.11 * t * 3.141592), sin(1.0 / 13.12 * t * 3.141592));

    vec2 size = vec2(0.2, 0.2) * 1.0;
    vec2 uv_tv = (uv - 0.5) / size;
    uv_tv += 0.5;



    float k = 1.0;
    for (int i=0; i<2; i++) {

        uv_tv -= 0.5;
        uv_tv *= mix(vec2(1.0), 0.5 + 1.50 * length(uv_tv) * (1.0 - abs(uv_tv)), 0.0 + 0.9 * sin(k * t / 16.0 * 3.141592) / k);
        uv_tv += 0.5;

        k*= 2.0;
    }

    uv_tv -= tv_center;

    vec3 col_bg = trace(2.0 * (uv - 0.5) * iResolution.xy / iResolution.yy, t);

    vec3 col_top = fract(texture(iChannel3, uv_tv).rgb + texture(iChannel3, -2.0 * uv_tv).rgb);
    col_top = mix(col_top, vec3(1.0), 0.5)  * vec3(1.4, 1.0, 0.0);

    vec2 uv_tv_geom = uv_tv;

    vec3 col_bot = texture(iChannel2, uv_tv_geom).rgb;
    vec3 col_border = fract(texture(iChannel0, abs(fract(uv_tv + 0.3 * t / iChannelResolution[1].xy - 0.95) * 2.0 - 1.0)).rrr + texture(iChannel0, uv_tv).rrr);
    vec3 col_tv = texture(iChannel0, abs(fract(uv_tv_geom - vec2(0.0, t / 16.0)) * 2.0 - 1.0)).rgb;

    uv_tv_geom -= 0.5;

    vec3 col = col_bg;
    col = mix(col, col_top, in_box(uv_tv_geom * 0.65 + 0.5) * smoothstep(-0.25, 0.0, 1.8 - length(uv_tv_geom * iResolution.yy / iResolution.yy)) * smoothstep(0.9 - 0.1 - 0.1 * abs(fract(t/8.0) * 2.0 - 1.0), 0.9 - 0.1 * abs(fract(t/4.0) * 2.0 - 1.0), abs(fract(-uv_tv_geom.x + uv_tv_geom.y) * 2.0 - 1.0)  ));
    col = mix(col, col_bot, smoothstep(-0.04, 0.0,  0.75 - length(uv_tv_geom)));
    col = mix(col, col_border, smoothstep(-0.04, 0.0,  0.7 - length(uv_tv_geom)));    
    col = mix(col, col_tv, in_box(uv_tv));


    col =  mix(col, uv_tv.xyy, in_box(uv_tv));

    col = max(vec3(0.0), min(vec3(1.0), col));


    fragColor = vec4(col, in_box(uv_tv));
}

