    vec3 col_bg = texture(iChannel1, uv_tv).rgb;
    vec3 col_bg_dist = texture(iChannel3, uv + 4.0 * normalize(uv - 0.5) / iResolution.xy).rgb;
    vec3 col_tv = texture(iChannel2, vec2(1.0 - uv_tv.x, uv_tv.y)).rgb;
    col_tv = mix(col_tv, col_bg, step(0.5, col_tv.g / (0.001 + col_tv.r + col_tv.b)));


    col = mix(col, col_bg_dist, 1.0 - step(0.2, length(cola.rgb)));

    col = mix(col, col_tv, cola.a);

    fragColor = vec4(col, 1.0);
}

